}
.portal_block .block_content {
    background: #d3d3d3 none repeat scroll 0% 0%;
    padding: 10px;
    border-bottom: 2px solid #B5C1CE;
}
    .news_item, .news_expanded {
    padding: 7px 10px;
    background: #d3d3d3 none repeat scroll 0% 0%;
    color: #607B9B;
}
    .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    color: #2B4F7A;
    background-color: #d3d3d3;
    padding: 0px;
}
    .node .nodeLastPost {
    background-color: #d3d3d3;
    font-size: 11px;
    padding: 6px 20px;
    margin: 10px;
    position: absolute;
    top: 0px;
    right: 0px;
    line-height: 14px;
    white-space: nowrap;
    word-wrap: normal;
    overflow: hidden;
    width: 210px;
    height: 28px;
}
    .sidebar .secondaryContent {
    background: #d3d3d3 none repeat scroll 0% 0%;
    padding: 10px;
    border-bottom: 2px solid #B5C1CE;
    overflow: hidden;
}
    .breadcrumb {
    font-size: 11px;
    font-family: "Open Sans";
    background-color: #d3d3d3;
    margin-bottom: 4px;
    overflow: hidden;
    max-width: 100%;
    height: 30px;
}
    #taigachat_box {
    width: 220px;
    height: 300px;
    padding: 5px;
    margin: 5px -2px 0px;
    overflow-x: auto;
    overflow-y: scroll;
    font-size: 11px;
    background: #d3d3d3 none repeat scroll 0% 0%;
}
    #taigachat_box li {
    background: #d3d3d3 none repeat scroll 0% 0%;
    margin-bottom: 6px;
    padding: 0px;
}
    .textCtrl {
    font-size: 12px;
    font-family: "Open Sans";
    color: #2B4F7A;
    background: #d3d3d3 repeat scroll 0% 0%;
    padding: 3px;
    margin-bottom: 2px;
    border: 2px solid #000000;
    outline: 0px none;
    transition: border 150ms ease 0s;
}
    .quickReply {
    background-color: #b3b3b3;
    padding: 8px;
    border-bottom: 2px solid #B5C1CE;
}
    .redactor_box {
    font-size: 0px;
    background-color: #d3d3d3;
    border: 2px solid #E6E8EB;
    line-height: 0;
    position: relative;
    overflow: hidden;
    transition: border 150ms ease 0s;
}
    .redactor_toolbar {
    font-size: 0px;
    background: #d3d3d3  repeat-x scroll center top;
    padding: 0px 0px 0px 2px;
    margin: 0px;
    border-bottom: 1px solid #F2F2F2;
    position: relative;
    left: 0px;
    top: 0px;
    line-height: 0;
    list-style: outside none none;
    overflow: hidden;
}
    .forum_who_is_viewing_list {
    background-color: #d3d3d3;
    border-bottom: 2px solid #B5C1CE;
    padding: 10px;
    margin-bottom: 5px;
}
    .xenForm fieldset + .ctrlUnit, .xenForm .formGroup + .ctrlUnit, .xenForm .submitUnit {
    background-color: #d3d3d3;
}
    .breadcrumb .crust:hover a.crumb {
    color: #FF9600;
    background-color: #d3d3d3;
}
    .secondaryContent {
    background-color: #d3d3d3;
}
    .discussionListItems {
    padding: 5px;
    background-color: #d3d3d3;
}
    .discussionListItem {
    background-color: #d3d3d3;
}
    .discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: #d3d3d3;
}
    #rc_switch li.active a {
    color: #FF9600;
    background-color: #d3d3d3;
}
    .xenForm .ctrlUnit {
    background-color: #d3d3d3;
}
    }
