css:
@-moz-document domain("gnula.nu")
    {
#foxmenu, .barrasummases, .sidebar1, ul.democolumnas li a, .post h2 a {

    font-family: 'Roboto'!important;
}
body, .content, .cover {background: #001C29!important; 
}
.content{border-top:none!important;
}
.post a:link, .post a:visited {
    color: #777777 !important;
 }
.barrasoc{
background-image: none !important;
background: none !important;
}   
.post h2 a, .post h2 a:link, .post h2 a:visited{

    font-family: 'Roboto'!important; font-weight: bold!important; color:#ffffff!important;
}
ul.tabs li.activa a,ul.tabs2 li.activa a,ul.tabs3 li.activa a,ul.tabs4 li.activa a,ul.tabs5 li.activa a{
background:#53FBDB!important;
    color:#191919!important;
}
.contenedor_tab, .contenedor_tab2, .contenedor_tab3, .contenedor_tab4, .contenedor_tab5, .contenedor_tab6{
    background-image:none!important;
background:#191919!important;
}
ul.tabs li a:hover,ul.tabs2 li a:hover,ul.tabs3 li a:hover,ul.tabs4 li a:hover,ul.tabs5 li a:hover{
    background:#333333!important;
}
#foxmenu ul li a {
    background: #00121A!important; color:#ffffff!important;
    }
#foxmenu ul li a:hover, #foxmenu ul li.current_page_item a {
    background: #53FBDB!important; color:#191919!important;
   }
#foxmenu ul li {
    width: 135px!important;
   }  
.post .postmetadata{ background:none!important;
}
ul.tabs li a, ul.tabs2 li a, ul.tabs3 li a, ul.tabs4 li a, ul.tabs5 li a, ul.tabs6 li a, ul.tabs7 li a, ul.tabs8 li a, ul.tabs9 li a, ul.tabs10 li a, ul.tabs11 li a, ul.tabs12 li a{
border: none!important; 
border-width: 0!important;
-moz-border-radius: 0!important;
-webkit-border-radius: 0!important;
border-radius: 0!important; 
}
ul.democolumnas img{
display: none!important;
}
#content{
    width:70%!important;
}
#buscador{ visibility:hidden!important;
}
.post .postmetadataH,ul.democolumnas{background:none!important;
}
#wrapper{
    width:100%!important;
}
.par, .impar{background: #00121A !important; color:#53FBDB!important;
}
#commentform input{
background: #00121A !important; border:1px solid #00212F!important; height: 35px!important;
}
.comments textarea{background: #00121A !important; border:1px solid #00212F!important;
}
.numcomentario{color:#53FBDB!important;
}
.comments h4,.commgrad{
    background-image:none!important; color:#53FBDB!important;
}
.widget-content{
    width:100%!important;
}
.contenedor_tab, .contenedor_tab2{ border:none!important;
}
ul.democolumnas li a{
    border-radius:none!important; border-radius:0!important; -webkit-border-radius:0!important; -moz-border-radius:0!important; font-size:12px!important;
}
ul.democolumnas li a:hover{
    background:#E34F92!important;border-radius:0!important;-webkit-border-radius:0!important; -moz-border-radius:0!important;
}
#top{
}
.time{ display:none!important;
}
.post h2{
    font-size:22px!important;font-family: 'Roboto'!important;
}
.entry{font-family: 'Roboto'!important;}
.sidebar1 h2, .sidebar2 h2 {
    border-radius: 0!important; color:#777777!important;
    -moz-border-radius: 0!important;
    -webkit-border-radius: 0!important;
    background-color: #191919!important;
    border: #191919 solid 4px!important;
}
.barrasummases{ background-image:none!important; background: #53FBDB!important; width:100%!important; color:#191919!important;
}
ul.democolumnas li a {
    color: #d1cbc1 !important;
    font-size: 11px !important;
    font-weight: bold!important; text-transform: uppercase!important;  
}
.post {
    font: inherit!important;
}
body{ font-size:15px!important; color:#fff!important; 
}
td{ background:#001C29!important;
}
.title{ background:#1F3742!important;
}
#top {
    height: 350px;
    margin-top: 0px;
    background: url(https://www.subeimagenes.com/img/gnulahead-2045980.png) #00202F!important;
    background-repeat: no-repeat!important;
  background-position: 50%!important;
}
#footer{background:#00121A!important; color:#53FBDB!important; font-size:11px!important; font-family:Tahoma!important;
}
#footer a{color:#ffffff!important;
}
#foxmenu ul {
    padding: 3px 0 0 0!important;
    }
#foxmenucontainer {
    height: 37px!important;
    background: #00121A!important;
      }  
#foxmenu {
    font-size: 17px!important;
    font-weight: bold!important;
    text-transform: uppercase!important;  
} 
#buscador {
    top: 7px!important;  
    }
                                        }
