body {
    font-family: "Lato";
}

#tab-bar {
    background: #303E4D;
    color: #b3b9bf;
    font-size: 15px;
    font-weight: 300;
    width: 220px;
}
.tab-list-shadow.bottom {
	background-image: linear-gradient(rgba(23,29,31,0), #27323f);
}
.nav-footer {
    width: 220px;
    background: none;
}
.nav-footer .nav-footer-tooltip {
    background: #27323f;
    width: 220px;
}
.nav-footer .nav-footer-actions {
    border: none;
}

.tab-link {
    color: #b3b9bf !important;
    line-height: 30px;
    max-height: 30px;
	padding: 0 0 0 20px;
    text-transform: lowercase;
}
.tab-link:hover {
    color: #8e97a0;
    background: #4A5664;
}
.current .tab-link:hover {
    color: #fff;
}
.private-tab .tab-link {
	padding: 0 0 0 20px;
}

.tab-menu-link {
    margin-top: 5px;
	right: 5px;
	color: #fff !important;
    fill: #fff;
}

.current .tab-link,
.current .tab-name {
    color: #fff;
    background: #6698C8;
}

.nav-header, .tab-link, .tab-name {
    background: none;
}

.flow-list .org-link {
	padding: 0 0 0 20px;
}

.org-name {
    color: #fff;
}

.tab-avatar {
    display: none !important;
}
.current .tab-avatar {
    background-color: #798BA0 !important;
}
.bubble-container .drag-handle {
    border-left: solid 7px;
    border-color: inherit;
}
.bubble-container .bubble-icon {
    opacity: 0;
}

/** Avatars **/
.user-avatar img,
.thread-avatar,
.user-menu-avatar {
    width: 30px;
    height: 30px;
	border-radius: 2px !important;   
}
.user-avatar::after,
.user-menu-avatar::after {
	border: none !important;
    box-shadow: none;
}

/** Threads **/
.chat-message {
    border: none !important;
}
.chat-message .content {
    line-height: 23px;
    margin-left: 0px !important;
}
.tag.mention {
	background: none !important;
    border: none !important;
    color: #007ab8 !important;
}
.tag.mention:hover {
    text-decoration: underline;
}
.chat-message .msg-body {
    font-size: 15px;
	color: #484a4e;
}
.message-author,
.emphasize-me .message-author {
    color: #2C2D30 !important;
    margin-right: 5px;
}
.timestamp-link time {
	position: relative !important;
	visibility: visible !important;
    color: #cccccc;
    font-size: 12px !important;
    font-weight: 300 !important;
	padding-left: 0;
    right: inherit !important;
}
.thread-avatar {
    width: 35px;
    height: 35px;
}
.avatar-container {
    left: 30px !important;
    top: 5px !important;
}

/** Message input **/
.message-form {
    background: #ffffff;
    border-top: none;
    padding: 20px;
}

.message-form fieldset {
	border-radius: 5px !important;
    border: 2px solid #E0E0E0;
}
.message-form.focused textarea {
    box-shadow: none !important;
}
.message-form.focused fieldset .left {
	box-shadow: none !important;
}

.expanding-input textarea,
.expanding-input pre {
    font-size: 15px !important;
    font-family: "Lato";
}
