css:
@-moz-document url-prefix("https://efusl.efupw.com/") {
@font-face {
  font-family: 'Federant';
  font-style: normal;
  font-weight: 400;
  src: local('Federant'), local('Federant-Regular'), url(https://fonts.gstatic.com/s/federant/v8/3UZxWoZanYkKkn3pE8qtMfesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
}

* { margin: 0; padding: 0; }

html {
	overflow-y: scroll;
	background: url('https://i.imgur.com/Mctx7Ve.jpg') no-repeat center center fixed #a9a8a8;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
body {
	font-family: 'Federant', cursive !important;
	font-size: 1.1em !important;
	text-align: center;
	color: #e3e6e6 !important;
	text-shadow: 0px -1px 0px #374683;
	padding-bottom: 2em;
}
ul { list-style: none inside; }
a { outline: none; }
a img { border: none; }
object {
	outline: none !important;
	z-index: 0 !important;
}
br {
	font-size: 0.001em !important;
}
.navbar td, form td {
	font-size: 95% !important;
}
.navbar a {
	text-decoration: none !important;
	color: #000 !important;
	text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #e8ecf7, 0 0 70px #e8ecf7, 0 0 80px #e8ecf7, 0 0 100px #e8ecf7, 0 0 150px #e8ecf7;
}

div.centered-table {
	position: relative !important;
	margin: 0 auto !important;
	width: 860px !important;
	line-height: 2em !important;
}
table.list {
	position: relative;
    background-color: transparent !important;
    border: none !important;
	width: 80%;
	margin: 0 auto;
}
tbody, tr, th, td {
	border: none !important;
	background: transparent !important;
	font-family: 'Federant', cursive !important;
	font-size: 105% !important;
}
th {
	font-size: 115% !important;
	padding: 10px !important;
}
td {
	text-align: center !important;
	padding: 5px !important;
}
.list a {
	font-weight: normal !important;
	color: #000 !important;
	text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #e8ecf7, 0 0 70px #e8ecf7, 0 0 80px #e8ecf7, 0 0 100px #e8ecf7, 0 0 150px #e8ecf7;
}
.good, span.good {
	color: #fff !important;
	font-family: 'Federant', cursive !important;
	font-size: 130% !important;
	font-weight: normal !important;
	text-shadow: 0 0 7px #fff, 0 0 15px #fff, 0 0 20px #fff, 0 0 30px #4d7eab, 0 0 40px #4d7eab, 0 0 50px #4d7eab, 0 0 80px #4d7eab, 0 0 100px #4d7eab;
}
.bad, span.bad {
	color: #131733 !important;
	font-family: 'Federant', cursive !important;
	font-size: 130% !important;
	font-weight: normal !important;
	text-shadow: #000 0px 2px 3px, #fff 0 -1px 2px;
}
a {
	text-decoration: none !important;
	color: #000 !important;
	text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #e8ecf7, 0 0 70px #e8ecf7, 0 0 80px #e8ecf7, 0 0 100px #e8ecf7, 0 0 150px #e8ecf7;
}

#col1, #col2, #col3, #col4 {
	text-align: left;
	margin-bottom: 2em;
}

}
