css:
@-moz-document url(http://www.nzherald.co.nz) {    
    #pd_div,
    #doublerectContainer,
    .box-ad,
    .pb-f-ads-ad,
    .ad-container,
    .pb-f-global-blank-html,
    #nav .pb-f-homepage-story {
     display:none !important;
    }   

    #main {
        margin-left: 0
    }

    #nav {
        position: relative;
        min-width: inherit;
    }

    #nav.compressed .pb-feature:not(.pb-f-navigation-site-logo) {
        display: none;
    }

    #nav:not(.expanded) .pb-f-navigation-site-logo .site-logo-wrapper .site-text {
        display: none;
    }

    #nav.compressed .pb-f-navigation-site-logo, #nav.compressed .pb-f-navigation-site-logo .site-logo-wrapper {
        display: block;
    }

    #nav:not(.expanded) .pb-f-navigation-site-logo .site-logo-wrapper {
        padding: 14px 10px 10px 14px;
        margin-top: 7px;
        width: 88px;
        height: 50px;
    }
    #nav:not(.expanded) .pb-f-navigation-site-logo .site-logo-wrapper {
        background: #000;
        position: fixed;
    }

    #nav:not(.expanded) .pb-f-navigation-site-logo .site-logo-wrapper .logo img {
        width: 18px;
        height: 23px;
    }

    #nav:not(.expanded) .pb-f-navigation-site-logo .site-logo-wrapper .close-btn .open-nav {
        display: block;
    }

    #nav:not(.expanded) .pb-f-navigation-site-logo .site-logo-wrapper .close-btn .nav-toggle {
        position: relative;
        top: 50%;
        transform: translateY(-50%);
        -webkit-transform: translateY(-50%);
    }

    #nav:not(.expanded) .pb-f-navigation-site-logo .site-logo-wrapper .close-btn {
        top: 0;
        right: 0;
        cursor: pointer;
        height: 100%;
        width: 42px;
    }

    .pb-f-global-nzme-header-bar .nzme-nav-bar .left-half-bar {
        padding-left: 95px;
    }

    .pb-f-global-nzme-header-bar .nzme-nav-bar .text-wrapper .section-name {
        max-width: 500px;
    }

    /* #nav.expanded {
        min-width: 310px;
        top: 65px;
        position: absolute;
    } */

    #nav.expanded .pb-f-navigation-site-logo .site-logo-wrapper .close-btn .close-nav {
        display: block;
    }

    .pb-f-navigation-site-logo .site-logo-wrapper .close-btn {
        top: 8px;
    }

    .pb-f-navigation-site-logo .site-logo-wrapper {
        padding: 5px 25px 25px 20px;

    }

    .pb-f-navigation-site-logo .site-logo-wrapper .logo {
        margin-right: 5px;
    }

    #pb-root {
        max-width: none;
    }

    .pb-f-bars-breaking-news-bar .bar-wrapper {
        display: block;
        height: 50px;
        position: fixed;
        bottom: 0;
    }

    .pb-f-bars-breaking-news-bar .bar-text {
        display: block;
        font-size: 16px;
    }

    .pb-f-bars-breaking-news-bar .bar-label {
        color: #333c3d;
        background: none;
        padding: 7px 0 0 17px;
        min-height: 22px;
    }

    .pb-f-bars-breaking-news-bar .breaking-news-item {
        top: 0;
        margin: 0 20px 0 17px;
        padding-left: 0;
        -webkit-transition: left 3.5s linear 2s;
        transition: left 3.5s linear 2s;
    }

    .pb-f-bars-breaking-news-bar .bar-label label span {
        display: inline;
    }

    .pb-f-bars-breaking-news-bar .bar-close .bar-close-btn {
        width: 50px;
        height: 50px;
        background-position: 15px 15px;
    }

    @media (min-width: 1269px) {
        .pb-f-navigation-site-logo .site-logo-wrapper.regional .regional-logo#regional-banner {
           display: none;
        }
    }

    .dark-theme.expanded .pb-f-navigation-site-logo .site-logo-wrapper.regional .regional-logo#regional-banner {
        display: none
    }
}
