css:
@-moz-document domain(mydramalist.com) {

.top-header {
	background-image: none !important;
    background-color: #82C1C1 !important;
}
#menu-main .menu>li>a {
    color: #ffffff;
}
#menu-main .menu>li>a:hover {
    color: #dbfcf4;
}

div.item:hover {
	background-color: #82C1C1 !important;
    background-image: none !important;                                 
}

.dropdown-menu {background: #dbfcf4;
	border: 1px solid #82C1C1;
}

.dropdown-menu>.item>a:hover {

    background-color: #8EBFBF !important;
    text-shadow: none;
}

img.avatar {
    border: 1px solid #dbfcf4 !important;
}

img[src*="http://i.mdldb.net/cache/rLQ/d/PLpGDA61_134798_s.jpg"] {
     border: 1px solid #dbfcf4 !important;
}

a, a:visited {
    color: #82C1C1;
}

a:hover {
    color: #61a5a5;
    text-decoration: none !important;
}

a :hover,a:visited :hover{
    color: #61a5a5;
    text-decoration:none !important
}

h2 {
    color: #82C1C1 !important;
}

#front_reviews li .warm_read {
	color: #82C1C1 !important;
}

.content-side .wrapper.secondary .more {
    color: #82C1C1 !important;
}

#front_social_icons {
    display: none;
}

.spnsr_right {
    display: none;
}

#content-top .spnsr-top {
    display: none;
}

.content-side .wrapper.secondary {
    margin-top: 0 !important;
    margin-bottom: 10px !important;
}
.section.dark h2 {
    background: #dbfcf4;
    text-shadow: none;
}

.footer-cr {
    color: #82C1C1 !important;
}

a.standard.btnManageList:hover {
    background-color: #82C1C1 !important;
}

.rec-text {
    border-top: 1px solid #dbfcf4 !important;
    background: #f2fffb !important;
}

#page-wmenu {
    background-color: #82C1C1 !important;    
}

#page-wmenu .page-item>a:hover {
    background-color: #61a5a5 ;
    background-image: none;
}

#page-wmenu .page-item.active>a:hover {
	background: #fff !important;
}

#page-wmenu .page-item.open>a, #page-wmenu .page-item.active>a {
    color: #82C1C1 !important;
}

h1 {
	color: #61a5a5;
}

.share-container.share-container--hright {
    display: none;
}

#dramalist .table th {
    background-color: #82C1C1;
}

#dramalist .table th>a {
	text-shadow: none !important;
}

i.fa.fa-caret-down:hover {
    color: #61a5a5 !important;
}

#page-wmenu .page-item.open>a,#page-wmenu .page-item.active>a {
	border-right: #61a5a5 !important;
    border-left: #61a5a5 !important;
}

#dramalist .table tr:nth-child(odd) {
    background-color: #f2fffb;
}

#page-wmenu .page-item>a {
    text-shadow: none;
}

.wrapper.primary>h2 {
    background-color: #82C1C1 !important;
    color: #fff !important;
    text-shadow: none !important;
    border-bottom: #61a5a5 !important;
}

.link {
    color: #61a5a5;
}

.dropdown-menu>li>a:hover {
    background-image: none;
    background-color: #82C1C1;
}

.progressBar {
    background-image: none !important;
    background-color: #82C1C1;
}

.progressBar.darker {
    background-color: #61a5a5 !important;
}

.rank {
    color: #61a5a5 !important;
}

#hub .leftContent .section {
    background-color: #f2fffb !important;
    border: #dbfcf4;
    }
    
.reviews .review .content-block .review-rating li:first-child {
    color: #61a5a5 !important;
    background-color: #f2fffb;
}

#searchFilters .filterToggle {
    color: #61a5a5 !important;
}

.button.primary, .button.primary.hover, .button.primary:hover{
    border-color: #61a5a5;
    background-color: #82C1C1;
}

#searchFilters .sectionEnd .block {
    background-color: #f2fffb;
}

a.btnManageList, .btn-managelist {
    background-color: #f2fffb;
    border-bottom: 1px solid #dbfcf4 !important;
}

#browse .actorBlock .content .statsInfo, #browse .userBlock .content .statsInfo, #browse .titleBlock .content .statsInfo {
    color: #61a5a5;
}

.content:hover>ul.statsInfo {
    color: #61a5a5 !important;
}

#searchFilters .filterOptions li label>i, #searchFilters .filterOptions li label.active i {
    background-image: url("http://i.imgur.com/UMUpdIM.png");
}

.content-side .wrapper.primary .content {
    background-color: #f2fffb !important;
}

.section.with-header>h2.bar {
    background-color: #82C1C1;
    color: #fff !important;
    text-shadow: none;                                        
}

.form_result_list th {
    background-color: #82C1C1 !important;
    color: #fff;
    text-shadow: none;
}

.form_result_list {
    border: 1px solid #82C1C1;
}

#schedule label {
    color: #82C1C1;
}

#settings_genres {
    background: #f2fffb;
}

label:hover {
    background: none !important;
}

.post__message .post__actions>a:hover {
    color: #61a5a5;
}

.page-list .fa {
	font-family: sans-serif !important;
}

li.row::before {
    font-family: 'icomoon' !important;
}

.blockquote {
    background-color: #f2fffb;
    border: 1px dashed #dbfcf4;
}

.section {
    font-family: sans-serif !important;
}

a.label {
    font-family: sans-serif;
}

.side-menu li a, .side-menu li a:hover {
    background-color: #82C1C1;
    text-shadow: none;
    color: #fff;
}

#terms .label, #about_us .label {
    color: #82C1C1;
}

#searchFilters .filterOptions li label.exclude i {
    background: url("http://i.imgur.com/UMUpdIM.png") -82px -201px no-repeat;
}

.side-menu.active-hover li:hover a, .side-menu li.active a {
    background-color: #fff;
    text-shadow: none;
    color: #82C1C1;
}

.content-side .wrapper.primary h2 .more {
    color: #f2fffb;
}

#userProfile .statsBlock.titleStats ul .progressBarWrapper .progressBar {
    border-radius: 2px;
}

#userProfile .statsBlock .header, #userProfile .statsBlock.titleStats ul .label, #userProfile .statsBlock.titleStats ul .num {
    color: #82C1C1;
}

#header .header-account .notifications h2 {
    border-bottom: #82C1C1;
}

#notifications-dropdown-menu li:hover, .notification-list li:hover {
    background-color: #f2fffb;
}

.account .search-dropdown, #notifications-dropdown {
    color: #f2fffb;
}

.content-side .wrapper.primary .content {
    border-right-color: #dbfcf4;
	border-left-color: #dbfcf4;
    border-bottom-color: #dbfcf4;
}

.search-main .btn_search i:hover {
    color: #82C1C1;
}

.icons-zoom:hover {
background: url('http://mydramalist.com/assets/img/icons-sd1f749a744.png') no-repeat;
    background-position: -64px 0;
}

#article-author .stats li .count {
    color: #61a5a5;    
}

#show-details h1 {
        color: #61a5a5;  
}

.show-rating {
    background-image: none;
    background-color: #61a5a5;
    text-shadow: none;
    box-shadow: none;
}

.ratingBar .bar .fill {
    background: url("http://i.imgur.com/qqMMtby.png")
}

.share-container.share-container--hleft {
    display: none;
}

.content-side .wrapper.primary {
    margin-top: 2px !important;
}

.recentlyWatchedBy {
    margin-bottom: 20px !important;
}

h2.bar .panel a:hover {
    color: #61a5a5;
}

.reviews .review .user-res .helpful:hover, .reviews .review .user-res .helpful.false:hover {
    background-color: #61a5a5;
    box-shadow: none;
    border-bottom: none;
    border-right: none;
}

.reviews .review .user-res .helpful, .reviews .review .user-res .helpful.false {
    border: none;
}

.reveal-spoiler {
    background-color: #61a5a5;
}

.comment .cmt_action_bar .liked:hover {
    color: #61a5a5;
}

span[style*="color: #E00606;font-size: 12px;border: 1px solid #D71717;padding: 0 5px;border-radius: 2px;"] {
    color: #fff !important;
    border: 1px solid #fff !important;
}

.button.green {
    background-color: #82C1C1;
    border-color: #82C1C1;
}

.tvShowCastList .cast:hover {
    background-color: #f2fffb;
}

.film-list .row:nth-child(odd) {
    background-color: #f2fffb;
}

.button.green:hover {
    background-color: #61a5a5;
    border-color: #61a5a5;
}

.reviews-sortby .sortby .dropdown-toggle {
    box-shadow: none;
}

#photos .photo {
    background-color: #f2fffb;
}

#photos .report:hover {
    color: #61a5a5;
}

.redactor_toolbar li a:hover {
    background-color: #61a5a5;
}

.discussions_trending__table a:visited {
    color: #82C1C1 !important;
}

.discussions_trending__table a:hover {
    color: #61a5a5 !important;
}

.discussions_trending {
    background-color: #f2fffb;
    border-bottom: none;
}

.discussions_trending__table a {
    color: #82C1C1;
}

.discussions_trending__hottest h2, .discussions_trending__recent h2 {
    border-bottom: none;
}

.discussions_trending__table td {
 	border-bottom: none;   
}

.thread-container h3, .discussion-container h3 {
    color: #61a5a5;
    background-color: #f2fffb;
}

.thread.odd, .discussion-listing .odd {
    background-color: #f2fffb;
}

#watch-online-now .slick-prev:before, #watch-online-now .slick-next:before {
    color: #82C1C1;
}

#watch-online-now .slick-slide>a>i:hover {
    color: #82C1C1;
}

.modal-header.primary {
    background-color: #82C1C1;
    border-bottom: 1px solid #61a5a5;
}

.button.link {
    color: #61a5a5;
}

.input textarea.focus, .input textarea:focus, .input .customSelect.focus, .input .customSelect:focus, .input input[type="text"].focus, .input input[type="text"]:focus, .input input[type="password"].focus, .input input[type="password"]:focus {
    border: 1px solid #82C1C1;
    box-shadow: none;
}

#ModalManageList a.btnRateEpisode {
    color: #61a5a5;
}

.button {
    box-shadow: none;
}

li.token-input-token-mdl {
    background: #f2fffb;
    border: 1px solid #82C1C1;
}

li.token-input-token-mdl span {
    color: #82C1C1 !important;
}

#notifications-dropdown-menu li.type_0 {
    background-color: #82C1C1;
    border-top: none;
}

#notifications-dropdown-menu li.type_0:hover {
    background-color: #61a5a5;
}

#notifications-dropdown-menu li.type_0 .info {
    color: #fff;
}

.list_warn {
    background-color: #f2fffb;
    box-shadow: none;
}

html {
    box-shadow: none;
}

#trailers .trailers-view li > a span {
    color: #61a5a5;
}

.html_content_block blockquote {
    background-color: #f2fffb !important;
    border: 1px dashed #dbfcf4;
}
  
}
