css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("new.hasznaltauto.hu") {
    #wrap,
    #adatlap,
    .modal-content,
    .adatlap-kepnezo-nagykep-kontener,
    .kepek-container,
    .container,
    .adatlap-kiajanlo,
    .tab-content,
    .talalati-sor,
    .talalati-sor-topad,
    .panel,
    .panel-szuro,
    .panel-heading,
    footer {
        background-color: /*[[bgC]]*/ !important;
    }
  
    .row,
    .adatlap-kiajanlo,
    h4,
    .btn.focus,
    .btn:focus,
    .btn:hover,
    a,
    a:hover,
    a:visited,
    a:focus,
    a:active {
        color: /*[[fgC]]*/ !important;
    }
  
    .tab-container.touch-tab.adatlap a {
        color: /*[[bgC]]*/ !important;
        background-color: /*[[fgC]]*/;
    }
  
    .tab-container.touch-tab.adatlap li.active>a,
     .breadcrumb {
        color: /*[[fgC]]*/ !important;
        background-color: /*[[bgC]]*/;
    }
  
    .hirdetesadatok tr:nth-child(2) td:nth-child(2),
    .hirdetesadatok tr:nth-child(5) td:nth-child(2),
    .hirdetesadatok tr:nth-child(9) td:nth-child(2),
    .hirdetesadatok tr:nth-child(17) td:nth-child(2),
    .hirdetesadatok tr:nth-child(18) td:nth-child(2),
    .hirdetesadatok tr:nth-child(25) td:nth-child(2) {
        color: red;
    }
      
    .felszereltseg-sor:after,
    .checkboxx-sor::after,
    .felszereltseg-sor::after {
        width: 0px;
    }
  
  
    @-moz-document domain(hasznaltauto.hu) {
        #adatlap, .hagomb-belso {
            user-select: text !important;
            -moz-user-select: text !important;
            -webkit-user-select: text !important;
        }
    }
}
