@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("corazondemelon.es"), domain("amordoce.com"), domain("sweetamoris.de"), domain("amoursucre.com"), domain("flirttistoori.com"), domain("csabitasboljeles.hu"), domain("slodkiflirt.pl"), domain("sweetflirt.ro"), domain("sladkiiflirt.ru"), domain("askito-m.com"), domain("sweetcrush.co.uk"), domain("mycandylove.com") {
index > .connected {
       background-color : #FFFFFF !important;
       background-image : url("http://img00.deviantart.net/345d/i/2017/072/0/9/fondo_by_darknessblack-db26rf6.jpg") !important ;
       background-repeat : no-repeat !important ;
       background-position : center center !important ;
       background-attachment : fixed !important ;
       background-size : cover !important ;
    }    
        #site-logo > a > img[src*="/logo-as.png"] {
           height : 0 !important ;
           width : 0 !important ;
           background: url("http://orig08.deviantart.net/494b/f/2017/163/7/4/logo_by_darknessblack-dbcfwru.png") no-repeat !important ;
 padding-left : 427px !important ;
           padding-top : 142px !important ;   
             
        }
    
       .halo {background-image : url("http://img15.hostingpics.net/pics/250980SansHalo.png") !important ;}
   
       panel-connected {background : #E8FCFD !important ;}

       #pseudo-container > .blue {
                background : #3B8487 !important ;
                box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
       }
       .title-container a {
                color : #FFFFFF !important ;
                text-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
       }
    
        #episode-container {background : #CBE7F3 !important ;}
        #episode-container span {color : #90AEB9 !important ;}
        #episode-container .number {color : #FFFFFF !important ;}
    
       .trombone {background-image : url("http://www.amoursucre.com/image/paper-clip/left.png") !important ;}
    
        #currencies-container .values {
           background : #C3DBF3 !important ;
           border : 2px solid #FFFFFF !important ;
           color : #FFFFFF !important ;
        }
    
        #panel-menu a {
            background : #90AEB9 !important ;
            color : #FFFFFF !important ;
        }   
    navigation-menu > div > .pink, navigation-menu > div > .blue {
        background : #C7EBF4 !important ;
        color : #FFFFFF !important ;
    }

    navigation-menu > div > .border {
        border : none !important ;
    }

    side-links > .link-entry {
        background : #5D9FBB !important ;
    }
    body, a, a:hover {
       cursor:url(http://orig07.deviantart.net/ba82/f/2017/079/9/2/cursor_by_darknessblack-db30urk.png), auto;
    }
}
