css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("webnovel.com") {
	.det-hd, .cha-page-in, .cha-lst, .hom-rec-1st .hom-thumb i, .hom-rec-1st .hom-thumb span, .hom-rec-1st .hom-thumb::after, .hom-rec-1st .hom-thumb i::before, .hom-rec-1st .hom-thumb i::after
	{ background-color: #121212 !important; }
	.m-streamer, .hom-books a img, .m-update th, .g_lang p, .g_wrap .g_bread, .g_thumb img, .det-h2, .ui-select-datalist-li, .g_msg_hd, .hom-bd::before, .g_drop_sel ._bd, .m-main-wrap, .m-main, .m-cell, .g_thumb img, .bt-cha-link, .g_mod_hd, .cha-modal-bd, .g_thumb, .mr5, .bt_fff
	{ background-color: #2D2D2D !important; }

	.g_header, .g_share a, .g_msg_bd ._readmore, h2.g_wrap, .mb5 .c_strong, .g_goTop, .g_sub_hd h2, .bt_lg, .cha-lst h3, .hom-bd .cf, .j_bookList .lst-item a:nth-of-type(2)
	{ background-color: #2C3E50 !important; }
	.g_lang p a:hover, .g_lang strong, .ui-select-button, .hom-sign .bt, .g_mod ._close, .lst-nav ._on, .det-info .bt_lg
	{ background-color: #CCCCCC !important; }
	.lst-chapter, .ui-select-datalist-li:hover, .g_msg .g_msg_bd, .g_drop_sel a:hover, .g_mod, .g_mod_login, .g_sub_hd, .cha-tit, .lst-item, .g_sub_hd .g_wrap, .g_footer
	{ background-color: #181818 !important; }

	.hom-bd::before, .g_mod, .g_mod_login._on, .g_thumb, .hom-rec-1st .hom-thumb i, .hom-rec-1st .hom-thumb span, .hom-rec-1st .hom-thumb::after, .hom-rec-1st .hom-thumb i::before, .hom-rec-1st .hom-thumb i::after, .bt_fff
	{ background-image: none !important; }
	.g_msg_bd ._readmore
	{ background: linear-gradient(to right,rgba(255,255,255,0), #2C3E50 1em) !important; }
	.cha-header .g_logo, .g_header .g_logo
	{ background-color: #E3232A !important; }
	
	[color="#000"], [color="#000000"], [color="Black"], .det-con-ol li a.c_strong:visited, .hom-sign p, .g_sub_hd ._shadow, .cha-catalog a:visited strong, .hom-rec li .c_strong
	{ color: #AAAAAA !important; }
	.m-update th, .m-update td span._tra, .g_wrap .g_bread, .det-h2, .g_drop_sel a:hover, .m-h1, .bt._f, .bt._t, .bt._g, .g_mod_hd, .cha-header .g_logo, .g_header .g_logo, svg.c_strong, .hom-h2
	{ color: #FFFFFF !important; }
	.g_lang p a:hover, .g_lang strong, .ui-select-text, :root .ui-select-icon, .hom-sign .bt, .g_mod ._close, .lst-nav ._on, .det-info .bt_lg
	{ color: #000000 !important; }
	.g_wrap .g_bread a, .ui-select-datalist-li, .g_drop_sel ._item, .g_drop_sel a
	{ color: #E0BC2D !important; }
	.cha-catalog a strong
	{ color: #E0BC2D; }
	.hom-h1, .hom-h3, .g_star small, .det-info h2, .g_wrap .g_bread span.vam:last-child, .g_msg_bd ._readmore, .m-hd p, .g_score small, .cha-header-in .cha-hd h3 > span, .g_score small, .g_goTop, .g_sub_hd h2, .bt_lg, .cha-lst h3
	{ color: ORANGE !important; }
	
	[color="#00F"], [color="#0000FF"], [color="Blue"], [color="Navy"], [color="#000080"], [color="#0000F0"], [color="#0000ee"], .hom-ann small, .m-update td._l span._tim, .vam.ml10.c_small, .cha-tit h3, .cha-lst ._type
	{ color: #3399FF !important; }
	.m-update td span._aut, .g_msg_hd h3:first-child span, .cha-tit p
	{ color: #2FC95C !important; }
	p#continued strong span
	{ color: #E3232A !important; }

	.m-streamer, .hom-banner, .hom-bd::before
	{ display: none !important; }
	.cha-lst
	{ border: 0 !important; }
	.cha-page-in, .lst-item, .hom-rec-1st .hom-thumb::before
	{ border: 1px solid #2D2D2D !important; }
	.cha-tit
	{ border-bottom: 1px solid #2D2D2D !important; padding-bottom: 7px !important; }
	.hom-h1
	{ margin-bottom: 0 !important; text-indent: 10px !important; line-height: 1.8 !important; }
	.m-update th
	{ padding-top: 10px !important; text-align: center !important; }
	.cha-lst h3
	{ padding: 19px 5px !important; margin: 20px 0 20px 0 !important; }
	.cha-lst
	{ padding: 0 !important; }
	.cha-lst ul
	{ margin: 0 !important; }
	.cha-lst h4
	{ max-height: 80px !important; }

	.g_sub_hd ._shadow
	{ opacity: 0.2 !important; }
	.cha-page
	{ max-width: 75% !important; }
	.g_sub_hd h2
	{ padding: 0 10px !important; }

	.cha-content
	{ background-color: #121212 !important; color: #AAAAAA !important; }
	.cha-content p
	{ font-size: 24px !important; text-indent: 1em !important; line-height: 160% !important; letter-spacing: inherit !important; padding-bottom: 1em !important; }

	.g_msg_bd ._readmore
	{ font-size: 11px !important; }
	.hom-h1
	{ font-size: 24px !important; }

	body * { font-family: "Droid Sans Fallback", "苹方-简", "蘋方-簡", "蘋方-繁", "文泉驛微米黑", "WenQuanYi Micro Hei", "Noto Sans CJK TC Regular", "Microsoft YaHei UI", "Microsoft YaHei", "微软雅黑", Sans-serif; }
	body * { text-decoration: none !important; text-shadow: none !important; box-shadow: none !important; }
	html, body { color: #AAAAAA; background: #121212 !important; background-color: #121212 !important; background-image: none !important; }
	body h1, body h2, body h3, body h4, body h5, body h6, body ul, body li, body ol, body span, body p, body dt, body dd, body dl { background-color: transparent !important; }
	body h1, body h2, body h3, body h4, body h5, body h6, body th, body em, body b, body strong, label { color: #FFFFFF; }
	body div, body p	{ color: #AAAAAA; }
	body blockquote { color: #AAAAAA !important; background-color: #2D2D2D !important; background-image: none !important; }
	body textarea { color: #E0BC2D !important; background-color: #2D2D2D !important; background-image: none !important; }
	body table, body tr, body td { background-color: transparent; color: #AAAAAA; background-image: none !important; }
	body select, body option { background-color: #2D2D2D !important; color: #E0BC2D !important; }
	body input[type="text"] { color: #E0BC2D !important; background-color: #2D2D2D !important; }
	body button, body input[type="button"], body input[type="submit"] { color: #000000 !important; background-color: #CCCCCC !important; }
	body input[type="reset"], caption { color: ORANGE !important; background-color: #2C3E50 !important; }
	body a { color: #E0BC2D !important; }
	body a:link { color: #E0BC2D; }
	body a:visited { color: #AAAAAA; }
	body a:hover { color: #3399FF !important; }
	body a:active { color: #423F3F !important; }
	lightbox * { background: transparent !important; background-color: transparent !important; }
}
