css:
@-moz-document domain("www.fimfiction.net")
{.content_format_wob .chapter {
    background-color: #2E2457;
    color: #E6EDFF;
    text-shadow: -1px -1px rgba(0, 0, 0, 0.2);
}

.chapter .chapter-title a {
    color: #E6EDFF;
}
.toolbar {
  background: rgba(255,255,255,0);
  opacity: 1;
  padding: 0.25em;
  border-bottom: 1px solid #40327a4d!important;
  border-top: 1px solid #40327a4d!important;
}
@-webkit-keyframes move-twink-back {
    from {background-position:0px 0px;}
    to {background-position:-1000px 1000px;}
}
[data-read]{
  background:#000 url(http://www.script-tutorials.com/demos/360/images/stars.png) no-repeat;
  background-attachment: fixed;
  z-index:0;
}
.content_format_wob {
  background:transparent url(http://www.script-tutorials.com/demos/360/images/twinkling.png) repeat;
  background-attachment: fixed;
  -webkit-transform: translate3d(0,0,0);
  -webkit-animation:move-twink-back 75s linear infinite;
  overflow: auto;
}
.chapter_content_box {
background: #0000!important;
border-left-color: #40327a4d!important;
border-right-color: #40327a4d!important;
}
.chapter{
background: #2e245733!important;
}
div#toolbar_padder{
	background: #2e245733!important;
}
.chapter-body .bbcode hr:after {
 opacity: 0;
}
.toolbar .cell>ul>li>a {
  background: #0000;
  color: #EEEEEE;
  border: 1px solid #40327a4d;
}
.story-top-toolbar .bookshelves li.unselected,#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.left > div.story_container.chapter-container > div.story-top-toolbar > div.right,#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.left > div.story_container.chapter-container > div.story-top-toolbar > div.right > div > a.dislike_button,#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.left > div.story_container.chapter-container > div.story-top-toolbar > div.right > div > a.like_button.like_button_selected > span,#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.left > div.story_container.chapter-container > div.story-top-toolbar > div.right > div > a.like_button.like_button_selected > i{
    background: #f000!important;
  	color: #FFFFFF!important;
}
#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.left > div.story_container.chapter-container > div.story-top-toolbar > ul > *{
    border: 1px solid #40327a4d!important;
}
#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.left > div.story_container.chapter-container > div.story-top-toolbar > div.right{
    border-top: 1px solid #40327a4d!important;
    border-left: 1px solid #40327a4d!important;
    border-right: 1px solid #40327a4d!important;
		background: #2e245733!important;
}
.story_container .story-top-toolbar{
    border-bottom: 1px solid #40327a4d!important;
}
#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.left > div.story_container.chapter-container > div.story-top-toolbar > ul > *{
    background-color: #0000009e!important;
}
#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.right.advert{
	width: 0px;
}
#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.right.advert > div{
  width: 230px!important;
  font-size: 1.8em;
  background: #fff;
}
#chapter_format > div.content.mobile-no-margin > div.chapter-page > div.right.advert > div > a{
  width: 300px!important;
  padding: 10px 100% 10px 3px;
  background: #fff;
}
div.chapter-bookmark.placed{
  background-color: #603dff
}
.toolbar .cell>ul>li>a:hover {
    background: #2e2457ba;
}
.rating_container .rating-bar{
    vertical-align: -webkit-baseline-middle;
    margin-bottom: 9px;
}
}
