css:
@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/"), url-prefix("http://calendar.google.com/calendar/"), url-prefix("https://calendar.google.com/calendar/") {


/*User Option = Hide Navigation Bar*/ 
    #mainnav {
        display: none !important;
    }

/* Hide footer */
#navfooter {
    display: none !important;
}


/*Autohide Googlebar
Adapted from  "Google Calendar - Dark Style" by devo
https://userstyles.org/styles/102256/google-calendar-dark-style */


#onegoogbar{
                z-index:3;
                -moz-transition-duration: .2s;
                -webkit-transition-duration: .2s;
                position:fixed !important;
                width: 100% !important;
                height: 30px !important;
                padding-top: 37px !important;
                padding-bottom: 30px !important;
                top: -120px !important;
                border-bottom: #DADADA 6px solid !important;
                margin-top: 24px !important;
}

/* Autohide left menu
Adapted from "Google Calendar - Autohide Tool Palette (Sept2011)" by aftermarketgirl
https://userstyles.org/styles/50430/google-calendar-autohide-tool-palette-sept2011
*/

#mothertable > #nav{
                z-index:2;
                -moz-transition-duration: .2s;
                -webkit-transition-duration: .2s;
                position:fixed !important;
                width: 200px !important;
                height: 100% !important;
                padding: 30px !important;
                background-color: white !important;
                opacity: 0 !important;
                left: -275px !important;
                border-right: #DADADA 5px solid !important;
                margin-left: 30px !important;
  margin-top: -20px !important;
}



/* Move calendar up and right */
div#mothertable > div#maincell > div#mainbody{
     margin-left: 5px !important;
     margin-top: -17px !important;
}

#calmaster {
    margin-right: -25px;
}

/* Move edit event splash message down so it's visible when headers are hidden. */
div.mbox-cont {
    margin-top: 20px !important; 
}

/* Move Quick Add right, so visible when using q keyboard shortcut */
div.qab-container.gcal-popup {
    margin-left: 250px !important;
    margin-top: -40px !important;
}

/*Move Edit Event screen down */
div.ep-ea {
    margin-top: 20px !important;
}
}
