css:
@-moz-document domain("www.vidlii.com") {
a {
    color: #ff9800;
}
}

@-moz-document domain("www.vidlii.com") {
.mn_featured_sct {
    min-height: 120px;
    background: url(https://cdn.vidlii.com/img/featured_bg.gif) repeat-x #DDD;
    border-bottom: 1px dashed #c57301;
    padding: 9px;
    overflow: hidden;
}
}

@-moz-document domain("www.vidlii.com") {
header li {
    display: inline-block;
    list-style: none;
    background: #a9c4f9;
    padding: 7px 25px 4px;
    border-top-left-radius: 7px;
    border-top-right-radius: 7px;
    font-weight: 700;
    margin: 0 4px 0 0;
}
}
