css:
@-moz-document domain("youtube.com") {
button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-has-icon.no-icon-markup.yt-uix-subscription-button.yt-can-buffer.yt-uix-button-subscribe-branded {
    height: 24px !important;
    border: 1px solid #ccc !important;
    width: 90px !important;
    margin-left: 5px !important;
    font-size: 11px !important;
    background-image: -moz-linear-gradient(top,#fff 0,#e0e0e0 100%) !important;
    background-image: -ms-linear-gradient(top,#fff 0,#e0e0e0 100%) !important;
    background-image: -o-linear-gradient(top,#fff 0,#e0e0e0 100%) !important;
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#fff),color-stop(100%,#e0e0e0)) !important;
    background-image: -webkit-linear-gradient(top,#fff 0,#e0e0e0 100%) !important;
    background-image: linear-gradient(to bottom,#fff 0,#e0e0e0 100%) !important;
    -moz-border-radius: 2px !important;
    -webkit-border-radius: 2px !important;
    border-radius: 2px !important;
    color: black !important;
}

button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-has-icon.no-icon-markup.yt-uix-subscription-button.yt-can-buffer.yt-uix-button-subscribe-branded:hover {
