    background-image: -o-linear-gradient(top,#fff 0,#d2d2d2 100%) !important;
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#fff),color-stop(100%,#d2d2d2)) !important;
    background-image: -webkit-linear-gradient(top,#fff 0,#d2d2d2 100%) !important;
    background-image: linear-gradient(to bottom,#fff 0,#d2d2d2 100%) !important;
    border-color: #333;
}

button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-has-icon.no-icon-markup.yt-uix-subscription-button.yt-can-buffer.yt-uix-button-subscribe-branded:active {
    -moz-box-shadow: inset 0 2px 2px rgba(0,0,0,.75) !important;
    -ms-box-shadow: inset 0 2px 2px rgba(0,0,0,.75) !important;
    -webkit-box-shadow: inset 0 2px 2px rgba(0,0,0,.75) !important;
    box-shadow: inset 0 2px 2px rgba(0,0,0,.75) !important;
    background-image: -moz-linear-gradient(top,#fff 0,#d2d2d2 100%) !important;
    background-image: -ms-linear-gradient(top,#fff 0,#d2d2d2 100%) !important;
