button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-has-icon.no-icon-markup.yt-uix-subscription-button.yt-can-buffer.yt-uix-button-subscribe-branded:after {
    margin-right: 0px !important;
    margin-left: -80px !important;
    background: no-repeat url(http://i.imgur.com/ble91Mz.png) -310px -115px !important;
    background-size: auto !important;
    width: 16px !important;
    height: 12px !important;
    color: #a9382e !important;
    content: '' !important;
    display: inline-block !important;
    vertical-align: middle !important;
