	border: 3px solid #1f72ad;
	clip-path: polygon(10% 0, 100% 0, 100% 70%, 90% 100%, 0 100%, 0 30%);
	transform: scaleX(0);
	transform-origin: 50%;
	transition-property: transform;
	transition-duration: 0.2s;
