	/* Create Account */
	
	display: inline-block;
	left: -27px;
	font-size: 9px;
	font-family: 'Russo One', sans-serif;
	transform: translateZ(0);
	position: relative;
	transition-property: color;
	transition-duration: 0.5s;
	text-transform: uppercase;
	background: none;
}
[class="g-opacity-transition frontConnectHero__cta sc-button sc-button-cta sc-button sc-button-medium signupButton"]:before {
	content: "";
	position: absolute;
	z-index: -1;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background: #3496DB;
	border: 3px solid #1f72ad;
	transform: scaleY(0);
	transform-origin: 50% 0;
	transition-property: transform;
	transition-duration: 0.5s;
	transition-timing-function: ease-out;
}
[class="g-opacity-transition frontConnectHero__cta sc-button sc-button-cta sc-button sc-button-medium signupButton"]:hover {
	color: white;
	font-size: 15px;
}
[class="g-opacity-transition frontConnectHero__cta sc-button sc-button-cta sc-button sc-button-medium signupButton"]:hover:before {
	transform: scaleY(1);
	transition-timing-function: cubic-bezier(0.52, 1.64, 0.37, 0.66);
}
