	/*FULL PLAYER TAG*/
	
	width: 100px!important;
	height: 20px!important;
	top: -10px!important;
	border-radius: 0px;
	border: none;
	text-transform: capitalize!important;
	text-align: left;
	text-overflow: ellipsis;
	clip-path: polygon(5% 0, 100% 0, 100% 75%, 95% 100%, 0 100%, 0 25%);
	background: linear-gradient(to right, rgba(0, 0, 0, 0), #333333);
}
[class="soundTitle__tag sc-tag"]:before {
	display: none;
}
.soundTitle__tag.sc-tag.sc-tag-small {
	/*DEFAUlT TAG*/
	
	right: 0px;
	text-transform: capitalize!important;
	text-align: left!important;
	text-overflow: ellipsis!important;
	border-radius: 0px;
	border: none!important;
	top: 0px!important;
	width: 150px!important;
	height: 81px!important;
	line-height: 17px!important;
	white-space: nowrap!important;
	position: absolute;
	font-size: 0.1px;
	overflow: hidden;
	z-index: -1!important;
	background: linear-gradient(to right, rgba(0, 0, 0, 0), #333333);
	clip-path: polygon(0 0, 90% 0, 100% 30%, 100% 70%, 90% 100%, 0 100%);
}
.soundTitle__tag.sc-tag.sc-tag-small:before {
	display: none;
}
.soundTitle__tag.sc-tag.sc-tag-small:after {
	/*SHINE*/
	
	content: "";
	position: absolute;
	top: -110%;
	left: -210%;
	width: 200%;
	height: 200%;
	opacity: 0;
	transform: rotate(130deg);
	background: rgba(255, 255, 255, 0.13);
	background: linear-gradient( to right, rgba(77, 77, 77, 0.13) 0%, rgba(77, 77, 77, 0.13) 77%, rgba(77, 77, 77, 0.5) 92%, rgba(77, 77, 77, 0.0) 100%);
}
.soundTitle__tag.sc-tag.sc-tag-small:hover:after {
	/*SHINE*/
	
	opacity: 1;
	top: 100%;
	left: 200%;
	transition-property: left, top, opacity;
	transition-duration: 0.7s, 0.7s, 0.15s;
	transition-timing-function: ease;
}
.soundTitle__tag.sc-tag.sc-tag-small:active:after {
	/*SHINE*/
	
	opacity: 0!important;
}
