/* songs */

.sc-tag,
.sc-tag:visited {
	background-color: #1d1e2a !important;
	border-radius: 0px !important;
	padding: 10px !important;
}
.sceneLayer,
.playbackTimeline {
	filter: grayscale(500%) !important;
}
.waveformCommentsNode {
	filter: hue-rotate(0deg) !important;
}
.sc-button-medium.sc-button-startstation::before,
.sc-button-medium.sc-button-message::before,
.sc-button-medium.sc-button-more::before {
	filter: invert(100) !important;
}
.commentPopover__username,
a.commentPopover__username:hover,
a.commentPopover__username:visited {
	opacity: 0 !important;
}
