css:
/* i really want this to be global */
body
{
	background-color: #383838;
	color: #FFFFFF;
	font: 17px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
a:link
{
	color: #fcfccf;
	text-decoration: none;
}
a:visited
{
	color: #a09c63;
	text-decoration: none;
}
a:hover, a:active
{
	color: #FFFFFF;
	text-decoration: underline;
}
.page
{
	background-color: #383838;
}
td, th, p, li
{
	font: 12pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.tborder
{
	background-color: #383838;
}
.tcat
{
	background-color: #383838;
	color: #FFFFFF;
	font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.tcat a:link
{
	color: #FFFFFF;
	text-decoration: none;
}
.tcat a:visited
{
	color: #fcf8c4;
	text-decoration: none;
}
.tcat a:hover, .tcat a:active
{
	color: #fffce5;
	text-decoration: underline;
}
.thead
{
	background-color: #383838;
	color: #EEEEFF;
	font: bold 14px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.thead a:link
{
	color: #EEEEFF;
}
.thead a:visited
{
	color: #EEEEFF;
}
.thead a:hover, .thead a:active
{
	color: #EEEEFF;
}
.tfoot
{
	background-color: #383838;
	color: #EEEEFF;
}
.tfoot a:link
{
	color: #EEEEFF;
}
.tfoot a:visited
{
	color: #EEEEFF;
}
.tfoot a:hover, .tfoot a:active
{
	color: #EEEEFF;
}
.alt1, .alt1Active
{
	background-color: #383838;
}
.alt2, .alt2Active
{
	background-color: #383838;
}
.alt3
{
	background-color: #383838;
}
.wysiwyg
{
	background-color: #383838;
	color: #FFFFFF;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
textarea, .bginput
{
	background-color: #CFCFCF;
	color: #000000;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.button
{
	background-color: #CFCFCF;
	color: #000000;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
select
{
	background-color: #CFCFCF;
	color: #000000;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
option, optgroup
{
	font-size: 11px;
	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.smallfont
{
	font: 8pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.time
{
	color: #FFFFFF;
}
.navbar
{
	font-weight: bold;
}
.highlight
{
	color: #FF0000;
	font-weight: bold;
}
.fjdpth0
{
	background-color: #F7F7F7;
	color: #000000;
}
.panelsurround
{
	background-color: #383838;
	color: #FFFFFF;
}
legend
{
	color: #FFFFFF;
	font: bold 12px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.vbmenu_popup
{
	background-color: #7F7F7F;
	color: #000000;
	border: 1px solid #0B198C;
}
.vbmenu_option
{
	background-color: #014990;
}
.sfont {
	font-size: 0px;
	color: #0F559A;
}

.midfont{font:14px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif}

