css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("github.com") {
  /*haader logged out*/
  body {
    min-width: 100px;
  }
  .site-header-nav{
    display: none
  }
  .header-logo-invertocat, .octicon .octicon-mark-github {
    position:absolute;
    top: -3px;
    left: 0;
  }
  header {
    padding: 0 !important;
    margin: 0 !important;
    width: 100%;
    max-height: 32px !important;;
  }
  .site-header-actions {
    line-height: 26px !important;
  }
  .site-header-link, .text-gray {
    font-weight: 300 !important;
    font-size: 12px;
  }
  .site-nav-container {
    max-width: 100% !important;;
  }
  .header-search {
    margin: 0;
    padding: 0;
  }
  .site-header .header-search-wrapper {
    margin: 0;
    border-radius: 0;
    border-top: 0;
  }
  .header-search-scope {
    border-radius: 0;
  }
  .site-header .header-search-input  {
    max-height: 30px;
  }
  .site-header-menu {
    max-height: 32px !important;;
  }
  /*header logged in*/
  .header{ 
    padding:0;
  }
  [href^="/marketplace"]{
    display: none
  }
  .header-search {
    margin-left: 32px;
  }
  /*general*/
  .container {
    width: 100%;
  }
  /*page header*/
  .pagehead {
    padding-top: 1px;
    margin-bottom: 1px;
  }
  .repohead .repohead-details-container {
    margin-bottom: 8px; /*may become misaligned if less*/
  }
  .reponav-item {
    padding: 0 5px 0 5px;
  }
  .numbers-summary a, .numbers-summary .nolink, .pb-3 {
    padding: 0 !important;;
  }
  .stats-switcher-viewport {
    height: initial !important;
  }
  .btn  {
    padding: 2px !important;
  }
  .social-count {
    padding-top: 1px;
  }
  .select-menu-modal{
    width: 200%;
    max-width: 300px;
    overflow:hidden;
  }
  .file-navigation.in-mid-page, .table-list-header {
    margin-top: 0px;
  }
  .table-list-header {
    border-left: 0;
    border-right: 0;
    border-radius: 0;
  }
  .repository-lang-stats ol.repository-lang-stats-numbers li {
    padding: 1px;
  }
  /*stuff*/
  div .mb-3, .file-navigation .branch-select-menu, .file-navigation .BtnGroup, .overall-summary, .file-wrap, .boxed-group, .breadcrumb, .gh-header.issue .gh-header-meta, .subnav, .paginate-container, .tabnav-pr, .mb-1, .mt-1, .topic-tag, .mb-4, .mt-2, .mb-3{
    margin-top: 0 !important;
    margin-bottom: 0 !important;
  }
  table.files td {
    padding: 2px;
  }
  /*page*/
  .file {
    margin: 0
  }
  .file-wrap {
    border: 0 !important;
  }
  table.files td.age, table.files td.icon, table.files td .css-truncate, table.files td.message, table.files td.content {
    width: initial !important;;
    max-width: initial !important;;
    overflow-y:hidden;
    max-height: 25px;
    white-space:unset;
    padding: 0;
  }
  div .readme .markdown-body, .file{
    padding: 0px !important;
    border-left: 0;
    border-right: 0;
    border-bottom: 0;
  }
  .comment-body{
    padding: 2px;
  }
  .discussion-timeline {
    position:absolute;
    width: 100% !important;;
    padding-right: 200px !important;;
  }
  .topic-tag{
    padding-top: 1px;
    padding-bottom: 1px;
  }
  .boxed-group > h3, .boxed-group .heading, .overall-summary, .commit-tease, .commit-tease-contributors, .file-header, .gh-header-meta, .p-2, .table-list-header .btn-link, .timeline-comment-header-text, .p-3, .py-4{
    border-left: 0;
    border-right: 0;
    padding: 1px !important;;
    margin: 0px;
    border-radius: 0;
  }
  .border {
    border-left: 0 !important;;
    border-right: 0 !important;;
  }
  .container-lg {
    width: 100%;
    max-width: initial;
  }
  div.border-right{
    border-right: 0 !important;;
  }
  div.border-left {
    border-left: 0 !important;;
  }
  .file-info {
    line-height: initial;
  }
  .btn-octicon {
    padding: 1px;
  }
  h2.f4.mb-3.text-normal{
    display: none !important;
  }
  /*footer*/
  .site-footer, .protip, .discussion-timeline-actions {
    display: none;
  }
  .user-nav {
    margin: 0;
  }
  [itemprop^="about"] {
    padding-left: 1px;
    font-weight: 600
  }
}
