css:
/* For use in http://analysis.sesse.net/ 
Reach me out at twitter.com/twinysam */
@-moz-document url(http://analysis.sesse.net/) {
body {
    font-family: 'Source Sans Pro', sans-serif;
    background-color: #30383c;
    color: white;
    margin: 0 2.5%;
}
h1,
h2,
h3 {
    font-weight: 300;
}
h1 {
    font-size: 3em;
    margin-top: 0.5em;
}
h3 {
    margin-top: 1em;
    margin-bottom: 0;
}
a {
    color: #94d7f5;
}
#score {
    font-size: x-large;
    margin-top: 0;
}
.window {
    position: absolute;
    width: 0px;
    height: 0px;
    opacity: 0.0;
}
#pv,
#refutationlines {
    font-family: 'Droid Sans Mono', monospace;
}
.c1 {
    opacity: 0.75;
}
.l1arrow {
    opacity: 1.0;
}
.hidden {
    display: none;
}
.vir path {
    opacity: 0.0;
}
.vir path.l1arrow {
    opacity: 1.0;
}
#credits {
    font-size: smaller;
}
td {
    vertical-align: top;
}
p {
    margin-top: 0;
}
#sortbyscoreholder {
    font-size: smaller;
    margin: 0;
    font-style: italic;
}
#refutationlines {
    font-size: smaller;
}
#refutationlines .move {
    width: 3.5em;
}
#refutationlines .score {
    width: 3em;
    text-align: right;
    padding-right: 0.5em;
}
#refutationlines .depth {
    width: 2em;
    text-align: right;
    padding-right: 0.7em;
}
.board-b72b1 {
    border: 0.5em solid #636a6d;
}
.white-1e1d7 {
    background-color: #d5e1e8;
    color: black;
}
.black-3c85d {
    background-color: #a9b2b7;
    color: white;
}
.white-1e1d7.nonuglyhighlight {
    background-color: #cce5cf;
}
.black-3c85d.nonuglyhighlight {
    background-color: #9ab6a6;
}
#boardcontainer {
    width: 400px;
    margin-right: 2em;
    margin-bottom: 1em;
    float: left;
}
#board {
    display: block;
    width: 100%;
    padding: 0;
}
#hiddenboard {
    display: none;
}
#numviewers {
    display: block;
    width: 100%;
    text-align: center;
    font-size: smaller;
    margin-top: 0.5em;
    margin-bottom: 0;
}
#analysis {
    display: block;
    min-width: 400px;
    overflow: hidden;
}
/* If the board is too wide for the screen, shrink it to fit,
 * and then put the analysis below. */

@media all and (max-width: 400px) {
    #boardcontainer {
        width: 100%;
        float: none;
    }
    #analysis {
        min-width: 0;
    }
}
/* If there is almost no space for the analysis, shrink the board a bit, too. */

@media all and (min-width: 500px) and (max-width: 810px) {
    #boardcontainer {
        float: left;
        width: 50%;
    }
    #analysis {
        min-width: 0;
    }
}
a.move {
    color: #c5dce6;
    text-decoration: none;
}
a.move:hover {
    text-decoration: underline;
}
#linenav {
    display: none;
}
}
