@-moz-document domain("azlyrics.com") {
  
   /* Main page */
  
  body, .main-page, .lboard-wrap, .col-sm-offset-1 .panel {
    background-color: rgb(0,10,25);
	}
  
  .main-page {
    color: rgb(30,140,220);
  }
  
  .main-page ol, .album-panel, .form-control, .alert {
    background-color: rgb(15,30,50);
  }
  
  .alert {
    border-width: 0px;
  }
  
  .form-control, .navbar-default {
    border-color: rgb(50,50,50);
  }
  
  .comment, .div-share h1 {
    display: none;
  }
  
  .ringtone {
    padding: 20px;
  }
  
  a:link, .breadcrumb a:visited {
    color: rgb(0,80,200);
    text-shadow: 0px 0px 4px #000000;
  }
  a:visited {
    color: rgb(120,60,150);
  }
  
  .col-sm-6 .panel {
    filter: invert(100%) grayscale(100%) contrast(0.9);
  }
  
  .col-xs-6 img {
    filter: grayscale(50%) brightness(0.5) drop-shadow(0px 0px 5px rgb(0,0,0));
  }
  .col-xs-6 img:hover {
    filter: grayscale(0%) brightness(1) drop-shadow(0px 0px 5px rgb(0,100,200));
  }
  
  div.smt:nth-child(15) > small:nth-child(1), div.smt:nth-child(17) > small:nth-child(1) {
    color: rgb(40,50,70);
  }
  
  ::selection {
    color: rgb(100,200,250);
    background: rgb(0,30,80);
  }
  ::-moz-selection {
    color: rgb(100,200,250);
    background: rgb(0,30,80);
  }
  ::-webkit-selection {
    color: rgb(100,200,250);
    background: rgb(0,30,80);
  }
  
  
   /* Nav bars */
  
  .navbar-default {
    background-color: rgb(10,25,50);
  }
  
  .navbar-default .navbar-nav > li > a {
    color: rgb(0,120,200);
    text-shadow: 0px 0px 8px #000000
  }
  .navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus {
    color: rgb(0,180,220);
  }
  
  .navbar-header img {
    filter: grayscale(100%) brightness(0.5) contrast(2.5);
  }
  .navbar-header img:hover {
    filter: grayscale(100%) brightness(0.6) contrast(2.5);
  }
  
  /* Alphabet buttons */
  .btn-menu:link, .btn-menu:visited {
    background-color: rgba(20,20,20,0.8);
    border-color: rgba(0,0,0,0);
    color: rgb(0,100,180);
  }
  .btn-menu:hover {
    background-color: rgb(20,80,150);
    border-color: rgba(0,0,0,0);
    color: rgb(0,150,200);
  }
  
  
   /* Buttons */
  
  .btn-share {
    background-color: rgb(30,30,30);
    border-color: rgb(50,50,50);
    color: rgb(0,100,180);
  }
  .btn-share:hover, .btn-share:focus {
    background-color: rgb(50,50,50);
    border-color: rgb(50,50,50);
    color: rgb(80,150,220);
  }
  
  /* Search button */
  .btn-primary, .btn-xs {
    background-color: rgb(30,30,30);
    border-color: rgb(50,50,50);
    color: rgb(0,100,180);
  }
  .btn-primary:hover, .btn-xs:hover, .btn-primary:focus, .btn-xs:focus {
    background-color: rgb(50,50,50);
    border-color: rgb(50,50,50);
    color: rgb(80,150,220);
  }
  
  
   /* Hidden elements */
  
  div.col-xs-12:nth-child(2) > div:nth-child(11) { display: none; }
  div.noprint:nth-child(16) { display: none; }
  .footer-wrap, .navbar-footer { display:none; }
}
