#body-container {
    height: 100%;
    background: url(http://www.themesltd.com/backgrounds/random/sparkling_blue_stars.gif) fixed;
}

/*Identificador de creador del video*/
.comment-renderer-author-comment-badge.creator {
    background-color: #a900ff;
    border-radius: 9px;
    padding: 2px 248px;
    vertical-align: middle;
}
.comment-renderer-author-comment-badge.creator:hover {
    background-color: #FFFFFF;
    border-radius: 9px;
    padding: 2px 248px;
    vertical-align: middle;
}
/*Color de Enlaces de la Descripcion*/
#action-panel-details a {
    color: #03fdfd;
}

#action-panel-details:hover a {
    color: #03fdfd;
}
/*Color de Texto de la Descripcion*/
#watch-description {
    color: #fd0505;
    line-height: 14px;
    overflow: hidden;
    position: relative;
    z-index: 1;
}
/*Barra de Busqueda*/
#masthead-search {
    margin-top: 1px;
    max-width: 250px;
    overflow: hidden;
    padding: 0;
    position: relative;
    border-radius: 25px;
}
/*Iconos compartir redondos*/
.share-service-icon {
    vertical-align: middle;
    margin: -1px;
    border-radius: 100%;
}
/*Resumen de la barra de progreso*/
.resume-playback-progress-bar {
    background: blue;
    opacity: 1;
}
/*Color de la barra de Progreso*/
.ytp-play-progress {
    z-index: 34;
    background-color: blue;
}
/*Color Scruber de la barra de progreso*/
.ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
    background-color: blue;
}
/*Color de Progreso cargado en caché*/
.ytp-load-progress {
    z-index: 33;
    background: cyan;
}

/*Edicion de Reproductor de Video*/
.html5-video-player {
    position: relative;
    width: 100%;
    height: 100%;
    background-color: black;
    overflow: hidden;
    outline: 0;
    font-family: "YouTube Noto",Roboto,Arial,Helvetica,sans-serif;
    color: #eee;
    text-align: left;
    direction: ltr;
    font-size: 11px;
    line-height: 1.3;
    -webkit-font-smoothing: antialiased;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    touch-action: manipulation;
}

/*Iluminacion de barra de progreso -seleccion-*/
.ytp-hover-progress-light {
    background: rgba(199, 41, 218, 0.5);
}

.ytp-progress-bar {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 31;
    outline: none;
    background-color: #0e0e0e !important;
}

#masthead-search .search-btn-component {
border-color: #0008ff;
    background: #0423ff;
    color: #0008f9;
}

#masthead-search .search-btn-component:hover{
border-color: #ff0000;
    background: #ff0000;
    color: #ff1800;
}

/*Terminos de Busqueda de la Barra de Busqueda*/
#masthead-search-terms input {
    background: transparent;
    border: 0;
    display: inline-block;
    font-size: 11px;
    height: 100%;
    left: 0;
    margin: 0;
    outline: none;
    padding: 2px 6px;
    position: absolute;
    width: 100%;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
/*Margen videos de la izquierda*/
.video-list-item {
    position: relative;
    margin-bottom: 3px;
    border-radius: 9%;
}
/*Redondes Tumbs Derechos*/
.yt-uix-simple-thumb-related>img {
    border-color: #1D1AF1;
    position: relative;
    top: 0;
    border-radius: 9%;
}
/*Borde de video*/
#movie_player {
    z-index: 100 !important;
    border-radius: 5%;
}

.player-api {
    background: transparent;
}
/*Contenedor contador de suscriptores*/
.yt-subscription-button-subscriber-count-branded-horizontal {
    height: 22px;
    line-height: 24px;
    background-color: black;
}
/*Texto contenedor contador de suscriptores*/
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal {
    padding: 0 6px;
    color: #ef07bd;
    font-size: 15px;
    text-align: center;
}
/*Color de Borde de Caja de Comentarios*/
.comment-simplebox-renderer-collapsed-content {
    border: 1px solid #f905d0;
    color: #b8b8b8;
    cursor: pointer;
    margin-left: 11px;
    min-height: 28px;
    border-radius: 2px;
    padding: 10px;
}

/*Color de tail de caja de comentarios*/
.comment-simplebox-arrow .arrow-inner {
    border: 7px solid rgb(0, 0, 0);
    left: 2px;
    top: 1px;
    z-index: 1;
}

.comment-simplebox-arrow .arrow-outer {
    border: 6px solid rgba(249, 5, 208, 0);
    left: 0;
    top: 0;
}

.comment-simplebox-arrow .arrow-inner, .comment-simplebox-arrow .arrow-outer {
    border-bottom-color: rgba(0, 0, 0, 0);
    border-left-color: rgb(249, 5, 208);
    position: absolute;
}
/*Cabecera*/
#yt-masthead-container {
    background: url(http://www.themesltd.com/backgrounds/random/sparkling_blue_stars.gif) fixed;
    border-bottom: 1px solid #e62117;
    min-width: 0;
    padding-bottom: 8px;
    padding-top: 7px;
    position: relative;
    border-bottom-right-radius: 25px;
    border-bottom-left-radius: 25px;
    
}
/*Transparencia Barra Derecha*/
.yt-card {
    margin: 0 0 10px;
    border: 0;
    background: transparent;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}
    
    /*Color de Boton de Suscripcion sin Pulsar*/

    .yt-uix-button-subscribe-branded {
    color: #fefefe !important;
    background-color: #0400ff !important;
}
    
/*Color de Boton de Suscripcion por encima*/
.yt-uix-button-subscribe-branded:hover {
    background-color: #8d17ca !important;
}

/*Imagen de Usuarios Redondeadas a un margen de 100%*/
.yt-thumb img {
    font-size: 13px !important;
    outline: none !important;
    border-radius: 100%;
    background-color: transparent !important;
}
/*Imagen de Usuarios con fondo transparente*/
.yt-thumb {
    overflow: hidden;
    background: rgba(241, 241, 241, 0);
    font-size: 0;
    vertical-align: middle;
    display: inline-block;
}
/*Color de Texto*/
.comment-renderer-text-content {
    max-height: 65px;
    overflow: hidden;
    white-space: pre-wrap;
    word-wrap: break-word;
    margin-bottom: 8px;
    color: white;
}
/*Titulo del Video*/
#watch7-headline h1 {
    line-height: normal;
    word-wrap: break-word;
    color: lightcyan;
}
/*Miniaturas en la busqueda*/
.yt-ui-ellipsis {
    background-color: rgba(255, 255, 255, 0);
    display: block;
    line-height: 1.3em;
    overflow: hidden;
    position: relative;
    text-overflow: ellipsis;
    white-space: normal;
    word-wrap: break-word;
}
/*Curvatura de las imagenes de busqueda*/
.exp-search-big-thumbs #results .yt-lockup-tile .yt-thumb-simple img {
    top: 0;
    border-radius: 25px;
}
/*Menu Izquierdo Panel de Busqueda*/
#guide-container {
    width: 230px;
    padding: 13px 22px;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background-image: url(https://s-media-cache-ak0.pinimg.com/originals/81/0d/7f/810d7f716a950a63a9c0dba6aacb1a73.jpg);
}
/*Separador*/
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: 1px solid #0f21ef;
    box-shadow: none;
}
/*Scrollbar*/
.yt-scrollbar ::-webkit-scrollbar-thumb {
    background: #0423ff;
}
/*Scrollbar*/
.yt-scrollbar ::-webkit-scrollbar-thumb {
    background: #0423ff;
}
/*Scrollbar*/
.yt-scrollbar ::-webkit-scrollbar-thumb:hover {
    background: #0423ff;
}
/*Fondo Botones de Barra de Sentimientos*/
#watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover {
    background: blue;
    border-radius: 25px;
}
}

@-moz-document regexp("https://apis.google.com/u/0/_/hovercard.*") {
/*Avatar Cuenta Youtube*/
.xta .dOc {
    -webkit-box-shadow: rgba(0,0,0,.5) 0 4px 5px -1px;
    box-shadow: rgba(0,0,0,.5) 0 4px 5px -1px;
    margin-bottom: 0;
    margin-left: 13px;
    margin-top: 0;
    position: absolute;
    top: 0;
    border-radius: 100%;
}
/*Portada Cuenta Youtube*/
.qpd {
    background-color: rgb(51, 51, 51);
    background-size: cover;
    height: 96px;
    left: 0px;
    position: absolute;
    top: 0px;
    width: 350px;
    background-position: center center;
    border-radius: 25px;
}

* {
    background-image: url(https://s-media-cache-ak0.pinimg.com/originals/81/0d/7f/810d7f716a950a63a9c0dba6aacb1a73.jpg);
    color: #fff!important;
    border-color: #000!important;
}
}
