css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html url("http://www.w3.org/1999/xhtml");

:root {
--tab-bg: #000000 !important;                /* bookmarks bg */
--main-text: #fbf1c7 !important;      /* bookmarks fg */
}  


/* Anonymous elements that do nothing. */
#navigator-toolbox::after, #tabbrowser-tabs::before, .tabbrowser-tab::after, .tabbrowser-tab::before, .toolbar-primary::after {
    display: none !important;
}

#nav-bar {
    background: #fff !important;
    border: none !important;
    margin: 0 !important;
    padding: 0 !important;
    color: #000 !important;
}


/* #forward-button, #back-button {
display: none !important; 
}   
 */

/* menu button */
#PanelUI-button {
    box-shadow: none !important;
    margin-left: -5px !important;
    width: 24px !important;
    border: none !important;
}
/*
 *  Urlbar
 */

#urlbar-container { 
    min-width: 250px !important;
    background: inherit !important; /* color */
}

#urlbar-wrapper {
    margin: 0 !important;
    padding: 0 !important;
    background: transparent !important;
}

#urlbar {
    margin: 0 !important;
    color: #000 !important;
    padding: 0 !important;
    border: none !important;
    background: transparent !important;
}

#back-button, #forward-button {
/*     display: none !important; */
}

#ctraddon_extra-bar, #customToolbars, #PersonalToolbar,
#addon-bar, #nav-bar, #toolbar-menubar {
    padding: 0 !important;
    
}

/*
 *  Toolbars
 */

.toolbar-primary,
#TabsToolbar {
    height: 16px !important;
    padding: 0 !important;
    margin: 0 !important;
    background: #111 !important;
    -moz-appearance: none !important;
    transition: none !important;
}

#tabs-closebutton, #ctraddon_tabs-closebutton, #alltabs-button, 
#private-browsing-indicator {
    display: none !important;
}

/* 
 *  Tabs
 */

#tabbrowser-tabs {
    min-height: 0 !important;
    background: transparent !important;
}

.tabbrowser-tab {
    background: transparent !important;
    font-family: sans-serif !important;
    font-size: 12px !important;
    color: #bdae93 !important;
    border: none !important;
    padding: 0 0 0 0 !important;
}

.tabbrowser-tab > .tab-stack {
    padding: 0 0 0 11px !important;
}

.tabbrowser-tab[selected=true] {
    font-weight: bold !important;
    color: #fbf1c7 !important;
}

.tab-background-start, .tab-background-end, .tab-icon {
    display: none !important;
}

.tab-background-middle, .tab-background, .tab-content, 
.tab-text-stack, .arrowscrollbox-scrollbox {
    margin: 0 !important;
    padding: 0 !important;
    background: inherit !important;
}

#TabsToolbar toolbarbutton {
    -moz-appearance: none !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    padding: 0 1px 0 1px !important;
    margin: 0 !important;
    min-height: 0 !important;
    border: none !important;
    height: inherit !important;
}

toolbarbutton.tabs-newtab-button, 
toolbarbutton#new-tab-button {
    /* white plus icon */
    color: #fbf1c7 !important;
    fill: #fbf1c7 !important;
    list-style-image: url('data:image/svg+xml;charset=US-ASCII,<svg xmlns="http://www.w3.org/2000/svg" height="14" width="14"><path fill="%23fbf1c7" d="m7 11h-1v-1.5-1.5h-1.5-1.5v-1-1h1.5 1.5v-1.5-1.5h1 1v1.5 1.5h1.5 1.5v1 1h-1.5-1.5v1.5 1.5h-1z"/></svg>') !important;
    width: 12px !important;
}
