css:
/* i really want this to be global */
.header {
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: #f5f5f5;
    border-bottom: 1px solid #e5e5e5;
}
.header-dark {
    background-color: #f5f5f5;
}
.header-nav-link {
    display: block;
    padding: 4px 8px;
    font-size: 13px;
    font-weight: 600;
    line-height: 20px;
    color: #333;
}
.header-dark .header-nav-link {
    color: #333;
}
.header-dark .header-nav-link:hover {
    color: #4078c0;
    text-decoration: none;
}
.header-logo-invertocat .octicon-mark-github {
    float: left;
    font-size: 28px;
    color: #333;
    width: 28px;
    height: 28px;
}
/* .form-control,
.form-select */

.header-search-wrapper {
    height: 30px;
}

.js-site-search-form label.form-control {
    min-height: 34px;
    /* padding: 6px 8px; */
    
    font-size: 14px !important;
    line-height: 20px !important;
    color: #333 !important;
    vertical-align: middle !important;
    background-color: #fff !important;
    background-repeat: no-repeat !important;
    background-position: right 8px center !important;
    border: 1px solid #ddd !important;
    border-radius: 3px !important;
    outline: none !important;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.075) !important;
}
.form-control.focus,
.form-control:focus,
.form-select.focus,
.form-select:focus {
    border-color: #51a7e8 !important;
    outline: none !important;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.075), 0 0 5px rgba(81, 167, 232, 0.5) !important;
}
.header-search-wrapper {
    display: table;
    width: 100%;
    max-width: 100%;
    min-height: 0 !important;
    padding: 0;
    font-weight: normal;
    vertical-align: middle;
}
.scoped-search .header-search-scope {
    display: table-cell;
}
.header-search-scope {
    display: none;
    width: 1%;
    padding-right: 8px;
    padding-left: 8px;
    font-size: 12px !important;
    color: #767676 !important;
    white-space: nowrap;
    vertical-align: middle;
    border-right: 1px solid #eee;
    border-top-left-radius: 2px;
    border-bottom-left-radius: 2px;
}
.header-dark .header-search-scope {
    line-height: 26px !important;
}
.header-dark .header-search-input::-webkit-input-placeholder {
    color: #999 !important;
}
.header-dark .header-search-input::-moz-placeholder {
    color: #999 !important;
}
.header-dark .header-search-input:-ms-input-placeholder {
    color: #999 !important;
}
.header-dark .header-search-input::placeholder {
    color: #999 !important;
}
.header-nav-link:hover .dropdown-caret,
.header-nav-link:focus .dropdown-caret {
    border-top-color: #4078c0 !important;
}
.scoped-search .form-control.focus .header-search-scope {
    color: #4078c0 !important;
    background-color: #edf2f9;
    border-color: #c6d7ec !important;
}
.header-search-input {
    display: table-cell;
    width: 99% !important;
    min-height: 26px !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    font-size: 13px !important;
    background: none !important;
    border: 0 !important;
    box-shadow: none !important;
}
.header-search-input:focus {
    border: 0 !important;
    box-shadow: none !important;
}
.header-dark .header-search-scope {
    border-right: 1px solid #eee !important;
}
.user-nav .header-nav-link {
    height: 28px;
}
.header-search {
    float: left;
    width: 360px;
    margin-right: 10px;
}
.subnav-search-input {
    width: 320px;
    padding-left: 30px;
    color: #767676;
    border-color: #d5d5d5;
}
.input-contrast {
    background-color: #fafafa !important;
}
.input-contrast:focus {
    background-color: #fff !important;
}
.subnav-search-context+.subnav-search .subnav-search-input {
    border-top-left-radius: 0 !important;
    border-bottom-left-radius: 0 !important;
}
.header .header-search-scope {
    border-right-color: #dedede;
}
.header .header-search-scope {
    border-right-color: #dedede;
}
.header-nav-link:hover,
.header-nav-link:focus {
    color: #000;
}
.header-logo-wordmark {
    position: relative;
    float: left;
    height: 26px;
    margin-right: 15px;
    color: #201a1a;
}
