css:
@-moz-document domain("www.flowdock.com") {
.tab-link, .new-flow {
    padding: 0;
    overflow: hidden;
}
.tab-link .emojie {
    font-size: 15px !important;
    line-height: 20px !important;
}
.tab-menu-link, .tab-error-link {
    margin-top: 0;
}
.tab-link .activity-indicator, .new-flow .activity-indicator {
    right: 5px;
}
.tab-menu-link, .tab-error-link {
    right: 0;
}
.has-activity .tab-name {
    color: hsla(120, 73%, 75%, 0.8);
}
.tab-name {
    line-height: 20px;
}
.activity-indicator {
    display: none;
}
.org-link {
    display: none;
}
.org-flows {
    margin-top: 0;
}
.privates-list {
    margin-top: 1rem;
    padding-top: 1rem;
    border-top: 1px dashed hsla(0, 0%, 100%, 0.3);
}
.tab-list-shadow.top {
    display: none !important;
}
.chat-message {
    padding-top: 1px !important;
    padding-bottom: 1px !important;
}
.chat-message {
    border-color: transparent !important;
}
.chat-message.comment-group-hover {
    border-color: #e0e0e0 !important;
}
.chat-message.comment-group-hover + .chat-message {
    border-color: #e0e0e0 !important;
}
.chat-message.comment-group-hover + .chat-message.comment-group-hover {
    border-color: transparent !important;
}
.chat-message.last-read-message {
    background-image: linear-gradient(to left, hsla(120, 60%, 50%, 0.8) 0%, hsla(120, 60%, 50%, 0.8) 50%, transparent 50%) !important;
    padding-bottom: 10px !important;
}
.chat-message.last-read-message + .chat-message {
    padding-top: 10px !important;
}
.chat-message .last-read-message-text {
    color: hsla(120, 60%, 50%, 0.8);
}
.chat-message:last-child, .mention-message:last-child, .inbox-message:last-child, .error-message:last-child, .single-message:last-child, .thread-activity-message:last-child, .thread-discussion-message:last-child {
    box-shadow: none;
    padding-bottom: 10px !important;
}
.bubble {
    border-left-width: 10px !important;
    border-left-style: solid !important;
    padding-left: 2px !important;
    margin-top: -2px;
}
.bubble-icon[viewBox="0 0 20 20"], .bubble-icon[viewBox="0 0 20 20"] g {
    display: none;
}
.date-separator[class*=message]::before {
    border-left: 10px solid hsla(0, 0%, 0%, 0.15);
    border-top-color: hsla(0, 0%, 0%, 0.15) !important;
    bottom: 40px;
}
.indicators {
    right: 0;
}
}
