css:
/* twikcss by @4lex_kidd */

@-moz-document domain("twitter.com"){

.wtf-module , .roaming-module, .promoted-trend{
    display : none !important;
}

body:not(.edge-design) {
    background: #657786!important;
}

.Trends .context-trend-item {
    line-height: 12px;
    margin-bottom: 10px;
}

.AdaptiveMediaOuterContainer {
    margin-top: 10px;
    background-color: grey;
    z-index: 200;
    position: relative;
    width: 100%;
    height: 100%;
    opacity: 0.20;
}

.AdaptiveMediaOuterContainer:hover {
    margin-top: 10px;
    background-color: grey;
    z-index: 200;
    position: relative;
    width: 100%;
    height: 100%;
    opacity: 0.90;
}

.AdaptiveMedia {
    height: 50px;
    
}
.AdaptiveMedia:hover {
    height: 100%;
     z-index: 5;
}

.stream-item:not(.no-header-background-module) {
    background: #f5f8fa;
    border-left: 1px solid #e6ecf0;
    border-right: 1px solid #e6ecf0;
    background-clip: padding-box;
}

.Trends {
    height : 60px;
    overflow : hidden;
}

.Trends:hover {
    height : 100%;
}

.ProfileCard {
	margin-bottom : 8px;
}

.ProfileCard-bg{
    max-height : 10px;
}
.ProfileCard-bio{
    display : none;
}

.ProfileCard-avatarImage {
    border-radius: 5px;
    display: block;
    height: 50px;
    width: 50px;
}

body:not(.edge-design) .FollowStatus {
    text-transform: uppercase;
    background-color: #657786;
    border: 0;
    font-size: 11px;
    margin-left: 3px;
    font-weight: bold;
    color: #e6ecf0;
}

.FollowStatus {
    text-transform: uppercase;
    background-color: #657786;
    border: 0;
    font-size: 11px;
    margin-left: 3px;
    font-weight: bold;
    color: #e6ecf0;
}

.ProfileSidebar {
    display : none;
}

}
