@-moz-document domain("www.bet365.com") {
.bb-BettingBannerDetails, .wc-HomePage_SecondColumnContainer {display:none;}
video {width:600%!important;height:600%!important;}
.hls-HLSStreamingModule {height:486px!important; width:864px!important;}
.wl-PodModuleBase.bb-BettingBannersModule, .lv-ClosableTabViewContainer {
	transform:scale(1);
	transition:1s;
	transition-delay:0.5s;
}

.wl-PodModuleBase.bb-BettingBannersModule:hover{
	transition:1s;
	transition-delay:2s;
	transform:scale(2);
	position:relative;
	z-index:99999999;
	margin-left:50px; 
	margin-top:100px;
}

.lv-ClosableTabViewContainer:hover{
	transition:1s;
	transition-delay:2s;
	position:fixed;
	z-index:99999999;
        top:70px;
	right:0px;
	transform:scale(2);
}
}

@-moz-document domain("www.marathonbet.com") {
