css:
@-moz-document url-prefix("https://bbs.3dmgame.com/"){
#scbar_hot a,
#cs_right_bottom,
.wp.a_h,
.hm.xg1,
.wp.a_f{
display:none;
}
#nv_forum,
.pg_index,
#nv_home{
background:#f5f5f5;
}

.fl_g{
    padding: 10px !important;
}

.hd_table{
	border:none;
    background-image:url("https://www.3dmgame.com/page/images/logo.png");
    background-repeat: no-repeat;
    background-size:210px 49px;
    background-position-y: 6px;
    background-position-x: 25px;
    color:#9c9c9c;
}
.hd_table a{
color:#9c9c9c;
}
.hd_table td{
    background:none;
}
.hd_table td img{
	display:none;
}
#hd .wp, .wp.a_h{
    background:none;
}
#hd .pipe, #hd .h_menu a{
color: #000;
}
#hd{
    margin-top: -35px;
}
#hd .h_menu{
	padding-right:39px;
}
#scbar{
	border-top: 3px solid #00c9ff;
    border-bottom: 2px solid #00c9ff;
    margin-bottom: 10px;
    box-shadow: 2px 5px 10px 0 rgba(0, 0, 0, 0.1);
    background: #ececec;
    line-height: 28px;
    transition: all 350ms ease-in-out 0ms;
}
#scbar:hover{
    box-shadow: 2px 7px 10px 0 rgba(0, 0, 0, 0.15);
}
.wp {
    background: #f5f5f5;
}
.bm, .bn{
margin-bottom: 0px;
}
.m_pgt{
padding: 10px 25px 0;
}
#h_nv{
height:0px;
}
#h_nv ul{
top: 10px;
}
#h_nv li{
	background:#f5f5f5;
}
#h_nv a{
    border:1px solid #ccc;
    transition: all 150ms ease-in-out 0ms;
}
#h_nv a:hover{
	background:#fff;
}
.fl .bm_c{
    background: #f5f5f5;
}
.fl .bm_h{
    background:#ececec;
    border-top: 3px solid #cdcdcd;
}
.bm.lk{
background:none;
}
.bm.bml.pbn{
background:none;
    border:none;
}
.tshare.cl{
display:none;
}
.tbn li.a{
background: #f5f5f5;
}
.tb .a a{
background: #f5f5f5;
    border-bottom-color: #f5f5f5;
}
#newspecial,
#newspecialtmp,
#post_reply,
#post_replytmp{
	width:77px;
    height:33px;
    background:#00afde;
    text-align:center;
    line-height: 33px;
    color:#fff;
    font-size:14px;
    letter-spacing:2px;
    transition: all 150ms ease-in-out 0ms;
    margin:4px 0px;
}
#newspecial:hover,
#newspecialtmp:hover,
#post_reply:hover,
#post_replytmp:hover{
	background:#00c9ff;
	text-decoration:none;
}
#newspecial::after,
#newspecialtmp::after{
	content:'发新帖';
}
#post_reply::after,
#post_replytmp::after{
content:'回复';
}
#newspecial img,
#newspecialtmp img,
#post_reply img,
#post_replytmp img{
	display:none;
}
.bm.bw0.pgs.cl.m_pgt .z img{
	display:none;
}
.bm.bw0.pgs.cl.m_pgt > a{
	width:77px;
    height:33px;
    background:#00afde;
    text-align:center;
    color:#fff;
    font-size:14px;
    letter-spacing:2px;
    transition: all 150ms ease-in-out 0ms;
}
.bm.bw0.pgs.cl.m_pgt > a:hover{
	background:#00c9ff;
}
.bm.bw0.pgs.cl.m_pgt > a::after{
	content:'发新帖';
}
#postlist{
border-radius: 0px 15px 0px 0px;
}
.ts{
text-shadow: 4px 4px 8px rgba(0, 0, 0, 0.2);
}
.pls{
background: #f9f9f9;
}
.p_pop{
    border:#00c9ff 1px solid;
    background: #ececec;
    box-shadow: 4px 9px 10px 0px rgba(0,0,0,0.1);
}
.p_pop a:hover{
	background: #fff;
}
.scbar_icon_td,
.scbar_txt_td{
	background:none;
}
.scbar_txt_td input{
	border:1px solid #ccc!important;
    *border:none!important;
    padding:5px;
    height:18px;
    transition: all 450ms ease-out 0ms;
    font-size:13px!important;
    background:#f5f5f5;
}
.scbar_txt_td input:hover{
	border:1px solid #00c9ff!important;
}
.scbar_txt_td input:focus{
    background:#fff;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-name:greenPulse;
    -webkit-animation-duration: 3s;
}
@-webkit-keyframes greenPulse {
            from { border-color: #ccc;box-shadow:inset 0 0 10px #fff;}
            50% { border-color: #00c9ff;box-shadow:inset 0 0 7px #c1dbe2;}
            to { border-color: #ccc;box-shadow:inset 0 0 10px #fff;}
}
#mn_Na9bb a,
#mn_Nd605 a,
#mn_F303 a,
#mn_N2d81 a,
#mn_N6044 a{
	color:#999!important;
    font-weight:normal!important;
}
.xi1, .onerror{
	color: #0099c5;
}
.scbar_btn_td{
	background:none;
}
#scbar_btn{
    transition: all 450ms ease-out 0ms;
	background-color: #e2e2e2;
    background-image:url(data:image/png;base64,);
    background-repeat:no-repeat;
	background-position:center;
    width: 30px;
    height: 30px;
    border: 1px solid #ccc;
}
#scbar_btn:hover{
	background-color: #d9e8ec;
    border:1px solid #00c9ff;
}
.scbar_type_td{
    background:none;
}
.scbar_type_td a{
    height:28px;
    transition: all 450ms ease-out 0ms;
	background-color: #f5f5f5;
    background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAGxJREFUeNpi/P//PwMlgImBQjAMDGABEbNmzSJaQ1paGu28UADEf4H4Pxb8FyqP14AJQBwBxD/R1ID4kVB5goG4Gojdgfg9lP8eyl9FSiwcBGJbID4GxHZQPv5YwAKuArE1MYHIOJoXGAACDAA5lxxJ65L25wAAAABJRU5ErkJggg==);
	background-repeat:no-repeat;
    background-position:center right;
    border: 1px solid #ccc;
}
.scbar_type_td a:hover{
	border:1px solid #00c9ff;
}
.pg strong{
	background-color: #e2e2e2;
}
.pg a, .pg strong, .pgb a, .pg label{
	border-color: #cdcdcd;
}
.pg a:hover, .pgb a:hover{
    border-color: #00c9ff;
    color: #0099c5;
}
.ttp .a a{
	color: #0099c5;
    border-color: #cdcdcd;
    background:#ececec;
}
.ttp a:hover{
    border-color: #00c9ff;
    color: #0099c5;
}
.xi2, .xi2 a, .xi3 a{
	color: #555;
}
.pnc, a.pnc{
    background: #00afde;
    color: #fff;
    transition: all 150ms ease-in-out 0ms;
    border:none;
}
.pnc:hover, a.pnc:hover{
	background: #00c9ff;
}
.card .o a{
	background:#f5f5f5;
}
#mn_forum a{
	color:#00c9ff;
}



#mn_N0279,
#mn_Nbbfa,
#mn_Nce8d,
#mn_Nae94,
#mn_N7990{
    display:none!important;
}
}
