css:
@-moz-document url-prefix("http://devgaming.pl/") 
{
  html, body {
      text-rendering: optimizeLegibility;
      -webkit-font-smoothing: antialiased;
      font-family: Lato !important;
      background-color: #22232b;
      color: #b3b3b3;
  }

  /* i really want this to be global */
  /*////////////////////*/

  .maintitle {
      background: #090a0c;
      color: #e2e2e2;
      padding: 10px 10px 11px;
      font-size: 16px;
      font-weight: 300;
  }
  
   .userpopup dl {
      border-bottom: 1px solid #2e4dbb;
  }
  
   #folder_list .total {
      background-color: #090a0c;
  }
  
   .likepop.fixed_inner {
      background-color: #121317;
  }
  
   .topic_buttons li a:hover, .ipsButton:hover {
      transition: color .25s cubic-bezier(.25, .08, .44, 1);
      color: #fff !important;
      font-weight: bold !important;
      cursor: pointer;
      filter: opacity(80%) !important;
      background-color: #171717 !important;
      border-color: #4360c7 !important;
  }
  
   .popupClose {
      position: absolute;
      right: 16px;
      top: 12px;
      content: url(https://s14.postimg.org/8bsyigv81/iconmonstr-x-mark-1-16.png);
   }
  
   .ipsSideMenu ul li.active a {
      background: #2e4dbb url(http://devgaming.pl/public/style_images/dev/icon_check_white.png) no-repeat 6px 8px;
      color: #fff;
      font-weight: bold;
   }
  
  .bar {
      background: #090a0c;
  }
  
  #mainContainer {
      background-color: #191a1f;
  }

  #branding {
      padding-top: 10px;
      min-height: 190px;
  }
  
   .input_submit.alt {
      background: #090a0c !important;
      border-color: #090a0c !important;
       color: #aaa !important;
   }
  
   #billboardImage {
      border: 1px solid #090a0c !important;
      background: #090a0c !important;
  }
  
   cite.ipb {
      border-left: 2px solid #989898;
      background: #f6f6f6;
      background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
      background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#090a0c), color-stop(100%,#121317));
      background-image: -o-linear-gradient(top, #f6f6f6, #e5e5e5);
      border-bottom: 2px solid #121317;
      border-right: 2px solid #e5e5e5;
  }

  .maintitle a {
      color: #ffffff;
  }

  .ipsType_pagedesc {
      color: #efefef !important;
      line-height: 1.5;
  }

  .cke_skin_ips .cke_dialog_title {
      background: #2e4dbb !important;
  }

  .cke_skin_ips textarea.cke_dialog_ui_input_textarea {
      background-color: #292929 !important;
  }

  .cke_skin_ips div.cke_dialog_ui_input_text, .cke_skin_ips div.cke_dialog_ui_input_password, .cke_skin_ips div.cke_dialog_ui_input_textarea {
      border: 1px solid #191919;
      background: #292929 !important;
  }



  .cke_skin_ips input.cke_dialog_ui_input_text, .cke_skin_ips input.cke_dialog_ui_input_password {
      background-color: #292929 !important;
  }

  .cke_skin_ips .cke_dialog_contents {
      background-color: rgb(66, 66, 66);
      border-top: 1px solid #4e4e4e;
  }

  #bbcode-description {
      color: #b1b1b1 !important;
      white-space: normal !important;
      word-wrap: break-word;
  }

  .cke_skin_ips .cke_dialog_footer {
      background-color: #424242 !important;
      text-align: right;
  }

  .cke_skin_ips .cke_dialog_ui_input_select {
      background-color: #292929 !important;
      border: 1px solid #191919;
      padding: 3px;
  }

  .cke_skin_ips .cke_wrapper {
      padding: 0px 5px 0px 3px !important;
      border: 2px solid rgba(4, 4, 5, 0.29) !important;
      background-color: #121317 !important;
      background-image: none !important;
  }

  .cke_skin_ips .cke_toolbox {
      clear: both;
      background-color: #121317 !important;
      padding-bottom: 1px;
  }

  .cke_skin_ips *, .cke_skin_ips a:hover, .cke_skin_ips a:link, .cke_skin_ips a:visited, .cke_skin_ips a:active {
      color: #cacaca !important;
  }

      .cke_skin_ips .cke_contents iframe {
          background-color: rgba(0, 0, 0, 0.77) !important;
          border-color: rgba(0, 0, 0, 0.30) rgba(0, 0, 0, 0.30) rgba(0, 0, 0, 0.30) rgba(0, 0, 0, 0.30) !important;
      }

      .cke_skin_ips a.cke_toolbox_collapser, .cke_skin_ips a:hover.cke_toolbox_collapser {
          background-color: #484848 !important;
          border: 1px outset rgba(0, 0, 0, 0.6) !important;
      }

      .cke_top {
        background: none !important;
      }

      .cke_show_borders {
              color: #fff !important;
              background: rgba(0, 0, 0, 0) !important;
      }

  .CSS1Compat {
        background: rgba(0, 0, 0, 0) !important;
  }


  select {
      font: normal 13px helvetica,arial,sans-serif;
      color: #d8d8d8;
      background-color: #090a0c;
      outline: none;
      border-color: #000000;
  }

  .ipsType_subtitle {
      text-align: center;
      color: #fff !important;
      font-size: 18px;
      padding-top: 10px;
      padding-bottom: 10px;
      background-color: #090a0c;
  }

  .ipsSettings_sectiontitle {
      color: #ffffff;
  }

  .no_messages {
      background: rgb(23, 24, 29);
      color: #1c2837;
  }

  body#ipboard_body fieldset.submit, body#ipboard_body p.submit {
      padding: 15px 6px 15px 6px;
      text-align: center;
      background-color: #121317;
  }

  a {
      color: #a9a9a9;
      text-decoration: none;
  }

  .ipsBox_containerDev {
      background: #121317;
  }

  .desc, .desc.blend_links a, p.posted_info {
      font-size: 12px;
      color: #8a8a8a !important;
  }

  .desc.lighter, .desc.lighter.blend_links a {
      color: #6e6c77;
  }

  .ipsSideBlock {
      background: rgb(18, 19, 23);
      border: 1px solid rgb(9, 10, 12);
  }

  .statistics {
      background: rgb(18, 19, 23);
      border: 1px solid rgb(9, 10, 12);
  }

  #community_app_menu>li.active>a {
      border-bottom: 3px solid #2e4dbb;
  }

  #secondary_nav_app_info:hover {
      border-top: 3px solid #090a0c;
  }

  #secondary_nav_app_info {
      background: rgb(18, 19, 23);
  }

  #user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {
      color: #fff;
      background-color: #191a1f;
  }

  .ipsHeaderMenu {
      background: #191a1f;
      padding: 10px;
      overflow: hidden;
      width: 340px;
  }

  #user_link_menucontent #links li {
      text-shadow: none;
  }

  .input_text, .ipsTagBox_wrapper {
      color: #fff;
      outline: none;
      background: #090a0c;
      border: none;
  }

  .ipsButton_secondary {
     background: rgba(10, 10, 10, 0.68);
     color: #aaa;
     outline: none;
     transition: background .2s cubic-bezier(.56, .01, .73, .96);
     box-shadow: none !important;
     border: solid 1px #000;
     border-radius: 0 !important;
     opacity: 1 !important;
     filter: none !important;
     -webkit-transition: all 0.2s ease-in-out;
     -moz-transition: all 0.2s ease-in-out;
     -webkit-box-shadow: 0px 0px 47px -1px rgba(0,0,0,0.90);
     -moz-box-shadow: 0px 0px 47px -1px rgba(0,0,0,0.90);
     box-shadow: 0px 0px 47px -1px rgba(0,0,0,0.90);
  }

  .topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton {
      background: #090a0c;
      color: #aaa;
      border: solid 1px #000 !important;
      outline: none;
      transition: background .2s cubic-bezier(.56, .01, .73, .96);
      box-shadow: none !important;
      border-radius: 0 !important;
      opacity: 1 !important;
      filter: none !important;
      -webkit-transition: all 0.2s ease-in-out;
      -moz-transition: all 0.2s ease-in-out;
  }

  .ipsBox {
      background: #121317;
  }

  .message.unspecific {
      background-color: #191a1f;
      border-color: #090a0c;
      color: #d2d2d2;
  }

  .general_box h3 {
      background: #090a0c;
      color: #c5c5c5;
  }

  .ipsButton_secondary a {
      color: #bbbbbb;
  }

  .ipsVerticalTabbed_tabs>ul {
      border-top: 1px solid #090a0c;
      border-left: 1px solid #090a0c;
  }

  .ipsVerticalTabbed_tabs li.active a {
      border-right: 1px solid #090a0c;
      background: #121317;
      color: #fff;
  }

  .ipsVerticalTabbed_tabs li {
      background: rgb(9, 10, 12);
      color: #ffffff;
      border-bottom: 1px solid #090a0c;
  }

  .ipsVerticalTabbed_tabs li a:hover {
      background: #121317;
      color: #ffffff;
  }

  .ipsBox_container {
      background: #191a1f;
      border: 0;
  }

  .status_update {
      background: #191a1f;
  }

  .ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a {
      background: #2e4dbb;
  }

  .block {
      filter: blur(2px) !important;
  }

  .header {
      background: rgb(9, 10, 12);
      color: #a9a9a9;
  }

  .row1 {
      background-color: #090a0c !important;
  }

  .row2 {
      background-color: #101010 !important;
  }

  td, th {
      border-color: #272727;
  }

  .buyItem {
      border-top: none;
  }

  .rightBar {
      color: #fff;
      background: linear-gradient(#090a0c, #121317) !important;
  }

  .input_submit {
      background: #090a0c;
      color: #aaa;
      outline: none;
      transition: background .2s cubic-bezier(.56, .01, .73, .96);
      box-shadow: none !important;
      border: none !important;
      border-radius: 0 !important;
      opacity: 1 !important;
      filter: none !important;
      -webkit-transition: all 0.2s ease-in-out;
      -moz-transition: all 0.2s ease-in-out;
  }

  input[type=text]
  {
      background: #090a0c;
      color: #aaa;
      outline: none;
      transition: background .2s cubic-bezier(.56, .01, .73, .96);
      box-shadow: none !important;
      border: none !important;
      border-radius: 0 !important;
      opacity: 1 !important;
      filter: none !important;
      -webkit-transition: all 0.2s ease-in-out;
      -moz-transition: all 0.2s ease-in-out;
  }

  .upload
  {
      border: 1px solid #090a0c !important;
      background: #121317 !important;
      color: #d0d0d0 !important;
  }

  .rules
  {
      border: 1px solid #090a0c !important;
      background: #121317 !important;
      color: #ddd !important;   
  }

  textarea {
      background-color: #090a0c;
      outline: none;
      color:#fff;
      border-color: #000;
  }

  .message.error {
      background-color: #1d0000;
      border-color: #da788e;
      color: #a50125;
  }

  .colorDev2 {
      color: #737373;
  }

  ::-webkit-scrollbar-thumb {
      background: #333;
  }

  ::-webkit-scrollbar, ::-webkit-scrollbar-corner {
      background: #040406;
      width: 14px;
      height: 14px;
  }

  ::-webkit-scrollbar-button {
      display: none;
  }

  ::-webkit-scrollbar, ::-webkit-scrollbar-corner {
      background: #040406;
      width: 14px;
      height: 14px;
  }



  .popupWrapper {
      background-color: #2e4dbb;
      padding: 4px;
      -webkit-box-shadow: 0px 0px 30px rgb(45, 76, 185);
      -moz-box-shadow: 0px 0px 30px rgb(45, 76, 185);
      box-shadow: 0px 0px 30px rgb(45, 76, 185);
      -moz-border-radius: 4px;
  }

  .post_block {
      background: #121317;
      border-bottom: 1px solid #090a0c;
  }

  .post_body .post {
      color: #ececec !important;
  }

  ul.post_controls a:hover {
      color: #ffffff;
  }

  ul.post_controls a {
      color: #afafaf;
  }

  .cke_skin_ips .cke_contents iframe {
      background-color: #fff;
      border-width: 1px;
      border-style: solid;
      border-color: #000000;
  }

  .pagination .back a, .pagination .forward a {
      background: #090a0c;
      color: #dadada;
      border: none;
  }

  .pagination .back a:hover, .pagination .forward a:hover {
      background: #2e4dbb;
      border: none;
      color: #fff;
  }

  .pagination .pages li.active {
      background: #2e4dbb;
      color: #fff;
      font-weight: bold;
      border: none;
      padding: 1px 5px;
  }

  ul.mini_pagination li a {
      background: #121317;
      color: #fff;
      border: 1px solid #090a0c;
  }

  a:hover {
      color: #ddd;
  }

  .ipsVerticalTabbed_tabs li a {
      color: #8c8c8c;
  }

  .popupInner h3 {
      background: #090a0c;
      color: #fff;
  }

  #backtotop {
      background: #121317;
  }

  #backtotop:hover {
      background: #2e4dbb;
      color: #fff;
      opacity: 1;
  }

  #ips_photoWrap {
      border: 1px solid #090a0c !important;
  }

  .ips_photoPreview {
      border: 1px solid #090a0c !important;
  }

  li.ips_option {
      border: 1px solid #090a0c !important;
  }
  
   #billboardRules {
      border: 1px solid #090a0c !important;
      background: #121317 !important;
      color: #e6e6e6 !important;
  }

  #header_bar {
      padding: 0;
      text-align: right;
      background: #191a1f;
  }

  #community_app_menu>li>a:hover, #community_app_menu>li>a.menu_active {
      border-bottom: 3px solid #131519;
      color: #fafafb;
  }

  .ipsButton_secondary:hover {
      transition: color .25s cubic-bezier(.25, .08, .44, 1);
      color: #fff !important;
      font-weight: bold !important;
      cursor:pointer;
      filter: opacity(80%) !important;
      background-color: #171717 !important;
      border-color: #4360c7 !important;
  }

  .userpopup {
      background: #121317;
  }

  .popupInner h3 a {
      color: #ffffff;
  }

  .desc, .desc.blend_links a, p.posted_info {
      font-size: 12px;
      color: #ececec;
  }

  p.citation {
      font-size: 12px;
      padding: 8px 10px;
      border-left: 2px solid #090a0c;
      background: #f6f6f6;
      background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
      background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#090a0c), color-stop(100%,#121317));
      border-top: 2px solid #090a0c;
      border-right: 2px solid #090a0c;
      -moz-border-radius: 5px 5px 0 0;
      -webkit-border-radius: 5px 5px 0 0;
      border-radius: 5px 5px 0 0;
      font-weight: bold;
      overflow-x: auto;
  }

  blockquote.ipsBlockquote {
      font-size: 12px;
      padding: 10px;
      border: 2px solid #090a0c;
      border-left: 2px solid #0d0e10;
      -moz-border-radius: 5px;
      -webkit-border-radius: 5px;
      border-radius: 5px;
      background: #121317;
      margin: 0 0;
      overflow-x: auto;
  }
}
