css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
  
  .ProfileAvatar {
    border-radius: 12px !important;
  }
  
  .ProfileAvatar-image, .ProfileAvatar-placeholderImage {
    border-radius: 8px !important;
  }
  
  .DashboardProfileCard-avatarImage {
    border-radius: 7px !important;
  }
  
  .DashboardProfileCard-avatarLink,
  .ProfileCard-avatarLink,
  .Gallery.is-tweetless .Gallery-media {
    border-radius: 6px !important;
  }
  
  .avatar,
  .ProfileCard-avatarImage {
    border-radius: 5px !important;
  }
  
  .btn,
  .nav .session .dropdown-toggle::before,
  .ProfileCardMini-avatarImage {
    border-radius: 4px !important;
  }
  
  .size24,
  .Avatar,
  input, textarea, div[contenteditable], .t1-select {
    border-radius: 3px !important;
  }
  
  /* From here on I had to guess. */
  
  .DMAvatar {
    border-radius: 5px !important;
  }
  
  .EdgeButton, .EdgeButton:visited,
  .DMComposer-container {
    border-radius: 4px !important;
  }
  
  .RichEditor {
    border-radius: 3px !important;
  }
}

/* embedded tweets */
@-moz-document url-prefix() {
  
  /* .EmbeddedTweet.EmbeddedTweet--edge .TweetAuthor-avatar.Identity-avatar, *//* currently still set */
  .EmbeddedTweet.EmbeddedTweet--edge .follow-button {
    border-radius: 4px !important;
  }
  
  .EmbeddedTweet.EmbeddedTweet--edge img.Avatar {
    border-radius: unset !important;
  }
}
