css:
@-moz-document domain("bild.de") {
/* ==========================================================================
   BILD.DE DARK THEME (2018)
   v2.2.0

   https://github.com/sidneys
   sidneys.github.io@outlook.com
   ========================================================================== */

/* ====================================
   VARIABLES
   ==================================== */

:root
{
  --color-container-light: rgba(60, 60, 60, 1.0);
  --color-container-light-transparent: rgba(60, 60, 60, 0.0);
  --color-container-dark: rgba(50, 50, 50, 1.0);
  --color-container-darker-translucent: rgba(50, 50, 50, 0.7);
  --color-text-lighter: rgba(255, 255, 255, 1.0);
  --color-text-light: rgba(230, 230, 230, 1.0);
  --color-text-dark: rgba(190, 190, 190, 1.0);
  --color-text-accent: rgba(0, 148, 255, 1.0);
}

/* ====================================
   GLOBAL
   ==================================== */

html,
a
{
    color: var(--color-text-lighter);
    -webkit-font-smoothing: antialiased;
}

/* ====================================
   CONTAINER
   ==================================== */

#innerWrapper,
#outerWrapper,
.gallery,
.lead .col,
.lead .tsr4,
.lead .tsr7,
.lead .tsr7s,
.lead .tsr7t,
.lead .tsr9,
.lead .tsr9s,
.module,
body
{
    background-color: var(--color-container-light);
}

article,
main
{
    background-color: var(--color-container-light);
}

/* ====================================
   TYPO
   ==================================== */

#cover .headline,
.accordion-item h3,
.article-report__title,
.authors__name,
.authors__pubdate,
.cmt-report>h2,
.com-follow__title,
.countdown-timer .item .digit:first-child,
.crossheading,
.floatL .gallery .headline,
.floatL .gallery .kicker
.floatL .ktg .headline,
.floatL .tsr-fg .headline,
.floatL .tsr-fg .kicker,
.floatL .tsr-info>h3,
.floatL article .gallery-box .tsr11 .headline,
.floatL article .gallery-box .tsr11 .kicker,
.fonts-loaded .match-desc__result,
.fonts-loaded .match-desc__starttime,
.fonts-loaded .match-desc__team--away,
.fonts-loaded .match-desc__team--home,
.gallery .headline,
.headline, .newsticker-widget h3,
.interaction--content h2,
.ktg .headline,
.ktg-accordion .headline,
.ktg-entry h3,
.livestream .module-headline,
.newsticker-item h3,
.newsticker-sepatation,
.partnerteaser .tsr8 h4 .headline,
.stream-body .headline,
.subhead,
.thread-item .thread-heading,
.tsr-fg .headline,
.tsr-ktg .headline,
.tsr8 .entry-title .headline,
.tsr8 .partnerteaser h4 .headline,
.ubrpopup__headline,
article .gallery-box .tsr11 .headline,
section header a,
section header h2
{
    color: var(--color-text-lighter) !important;
}

.article-report p,
.article-report p.error,
.article-report p.success,
.conversion .icon,
.conversion-alt .icon,
.entry-content,
.hello .hint,
.hello ol,
.hello p,
.promotion,
.stream-body > p,
.stream-body blockquote cite,
.stream-body,
.tsr-ktg-txt .dtlist,
.tsr-ktg-txt .nrlist,
.tsr-ktg-txt > p,
.tsr-ktg-txt > ul,
.txt .dtlist,
.txt .nrlist,
.txt > p,
.txt > ul,
.txtstyle.ts01,
.ubrpopup__info,
.ubrpopup__kicker,
label
{
    color: var(--color-text-light);
}

.comment-list .fonts-loaded .member,
.countdown-timer .item .fonts-loaded .unit,
.fonts-loaded .comment-list .member,
.fonts-loaded .countdown-timer .item .unit,
.fonts-loaded .gallery .entry-content,
.fonts-loaded .tsr-fg .entry-content,
.fonts-loaded .tsr-mfb figcaption,
.fonts-loaded .tsr-multi-img .entry-content,
.fonts-loaded .tsr-singleimg figcaption,
.gallery .fonts-loaded .entry-content,
.tsr-fg .fonts-loaded .entry-content,
.tsr-mfb .fonts-loaded figcaption,
.tsr-multi-img .fonts-loaded .entry-content,
.tsr-singleimg .fonts-loaded figcaption
{
    color: var(--color-text-lighter);
}

.accordion-content p,
.accordion-content,
.authors,
.btn-text,
.cmt-report p,
.com-follow-content,
.com-form .com-textarea textarea,
.comment-actions li,
.comment-actions,
.comment-content,
.comment-list time,
.content-table th,
.credit,
.email,
.gallery .credit,
.gallery .index,
.gallery-box .credit,
.gallery-box .index,
.index,
.info,
.ktg-accordion .credit,
.ktg-entry li,
.ktg-entry p,
.ktg-entry ul,
.livestream-article .stream-body>time,
.match-meta,
.module .hint,
.newsticker-content,
.prohibit-info,
.prohibit-login,
.quote blockquote .txt-style-cite,
.quote blockquote cite,
.scoring-list,
.select,
.t5p3 .published,
.text,
.textarea,
.tsr-mfb .credit,
.tsr-multi-img .credit,
.tsr-singleimg .credit,
.txtstyle,
a.title-tip[title]:after,
article .hint,
form input.text
{
    color: var(--color-text-dark);
}

.tsr-mfb figcaption
{
    color: var(--color-text-lighter);
}

/* ====================================
   NAVIGATION
   ==================================== */

.main-nav
{
    background-color: var(--color-container-light);
}

.main-nav #logo img
{
    border-color: var(--color-container-light);
}

.main-nav .utilities a,
.main-nav .services a
{
    color: var(--color-text-light);
}

.main-nav .nav-ressorts a
{
    color: var(--color-text-lighter);
}

#breadcrumb
{
    background-color: var(--color-container-light);
    color: var(--color-text-light);
}

#breadcrumb h1,
#breadcrumb time,
#breadcrumb li > a
{
    color: var(--color-text-light);
}

#breadcrumb.bnews
{
    border-color: var(--color-container-light);
}

/* ====================================
   WIDGETS
   ==================================== */

.share-text
{
    color: var(--color-text-accent) !important;
}

.mediaplayer[data-theme="widget"] .media-poster {
    visibility: hidden !important;
}

.wetterwidget_title img
{
    -webkit-filter: brightness(200%) saturate(85%);
    filter: brightness(500%) saturate(50%);
}

.wetterwidget_name,
.wetterwidget_temp
{
    color: var(--color-text-lighter);
}

.billboard
{
    background-color: var(--color-container-light);
}

.newsticker-item, .accordion-item
{
    background-color: var(--color-container-light);
}

.accordion-item.is-active
{
    background-color: var(--color-container-dark);
}

.accordion-item.is-active,
.accordion-item:first-child
{
    border-top-color: var(--color-container-dark);
}

.regio-bar-2015 li a
{
    color: var(--color-text-lighter) !important;
}

.tripleblow h3 a
{
    color: var(--color-text-lighter) !important;
}

.endhome__wrapper .endhome__topbtn h2,
.endhome__wrapper .endhome__topbtn p
{
    color: var(--color-text-lighter);
}

.txt .tsr-mfb,
.txt .tsr-singleimg
{
    background-color: var(--color-container-dark);
}

header h2 a img
{
    -webkit-filter: brightness(50%) invert(100%);
    filter: brightness(50%) invert(100%);
}

.conversion,
.conversion:after,
.conversion:before,
.conversion-alt,
.conversion-alt:after,
.conversion-alt:before
{
    background-color: var(--color-container-dark);
}

.conversion p,
.conversion-alt p
{
    color: var(--color-text-lighter);
}

.conversion-page .txt:after
{
    background: linear-gradient(to bottom, var(--color-container-light-transparent) 25%, var(--color-container-light) 100%);
}

/* ====================================
   COVER OVERLAY
   ==================================== */

.cover
{
    background-color: var(--color-container-darker-translucent);
    -webkit-backdrop-filter: blur(5px);
    backdrop-filter: blur(5px);
}

.cover .close
{
    background-color: transparent;
}

/* ====================================
   FOOTER
   ==================================== */

footer h2,
footer .nav-footer > li a,
footer .nav-footer > li span,
footer .nav-footer > li > ul > li > a
{
    color: var(--color-text-light);
}

footer .nav-meta a
{
    color: var(--color-text-lighter);
}
}
