css:
@-moz-document domain("mixer.com") {

.viewers[_ngcontent-c30] {
	color: #fc3636;
}
.views[_ngcontent-c30],
.stream-title[_ngcontent-c30],
[_nghost-c29],
.tooltip[_ngcontent-c39] {
	color: #d7cceb !important;
}
.bui-btn[data-variant='default'].bui-btn-bordered[_ngcontent-c2],
.bui-btn[data-variant='primary'].bui-btn-raised[_ngcontent-c2]:not(.bui-btn-bordered) {
	border: 3px solid #121829;
	background: #14b866;
	border-radius: 5px;
}
.bui-btn[data-variant='default'].bui-btn-bordered[_ngcontent-c2]:hover {
	background: red;
}
.bui-btn[data-variant='default'].bui-btn-bordered[_ngcontent-c2]:before,
.bui-btn[data-variant='primary'].bui-btn-raised[_ngcontent-c2]:not(.bui-btn-bordered):before {
	font-family: 'Beam Icons';
	content: "\EA1F";
	color: white;
	font-size: inherit;
	font-weight: normal;
	font-style: normal;
	line-height: 1;
	text-transform: none;
	letter-spacing: normal;
	word-wrap: normal;
	white-space: nowrap;
	direction: ltr;
	-webkit-font-smoothing: antialiased;
	text-rendering: optimizeLegibility;
	-moz-osx-font-smoothing: grayscale;
	-webkit-font-feature-settings: 'liga';
	display: flex;
	-webkit-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	transition: .2s;
}
.bui-btn[data-variant='default'].bui-btn-bordered[_ngcontent-c2]:hover:before {
	content: "\EA1E";
}
.bui-btn[data-variant='primary'].bui-btn-raised[_ngcontent-c2]:not(.bui-btn-bordered):hover {
	background: #009F4D;
}
.heart[_ngcontent-c39] bui-icon[_ngcontent-c39],
bui-icon[_ngcontent-c50] {
	display: none;
}
.tooltip[_ngcontent-c39]::before,
.tooltip[_ngcontent-c39]::after {
	border: none;
}
.tooltip[_ngcontent-c39],
.channel-page[_ngcontent-c28] > aside[_ngcontent-c28] {
	background: none;
}
.tooltip[_ngcontent-c39]::before {
	display: inline-block;
	color: inherit;
	font-size: 14px;
	font-weight: 500;
	content: "Followers: ";
	margin-right: -6px;
}
.count[_ngcontent-c39] {
	margin-left: 65px;
	font-size: 14px;
	font-weight: 500;
}
.bui-btn-bordered.bui-btn-disabled[_ngcontent-c2] {
	pointer-events: none;
	border-color: transparent !important;
}
body > b-app > div.content.visible > b-channel-page-wrapper > router-outlet > b-channel-web-page > div > div.channel-page > section > div.profile-blocks.layout-row.layout-wrap.bui-padding-top-2x > div.flex-100.flex-gt-md-70.flex-gt-lg-80.layout-column.layout-column > bui-card > div:nth-child(2) > b-description-editor-panel > div > div > p:nth-child(1) > img {
	width: 1200px;
	height: 300px;
}

.interactive-controls.minimized[_ngcontent-c31] {
	margin-top: 10px;
}
}
