css:
@-moz-document domain("vidlii.com") {
.bottom_nav {
    background: linear-gradient(#ddd, #fff);
    box-shadow: none;
}

li#nav_sel {
    background: linear-gradient(#FCFCFC, #dedede);
    border-color: #D5D5D5; 
}
li#nav_sel a {
    color: #333;
    text-decoration: none;
}
header li a {
    color: #039;
    text-decoration: none;
}

header li {
    background: linear-gradient(#FBFCFF, #BBC7E6);
    border: solid 1px #C3D0EC;
    border-bottom: 0px
}

.h_middle {
    margin-top: 2px;
}
#pr_hd li {
    border-color: #C2D0EC;
    background: linear-gradient(#FBFCFF, #BBC7E6) !important;
    color: #039 !important;
    text-shadow: 1px 1px 0px #ffffff;
}
#pr_sel li {
    border-color: #D6D6D6 !important;
    background: linear-gradient(#FCFCFC, #D5D5D5) !important;
    color: #333 !important;
    text-shadow: 1px 1px 0px #ffffff;
}
.pr_hd_bar {
    background: linear-gradient(#D6D6D6, #E7E7E7);
}
#sm_nav a {
    color: #03c;
}
}
