css:
@-moz-document domain("linfo.org")
{
  
  @font-face
  {
    font-family: 'Bask';
    font-style: normal;
    font-weight: normal;
    src: url('https://file.io/PuD7HP') format("opentype");
  }
  html, body{
    background-color: #222;
  }
  p{
    font-family: 'Bask' !important;
    text-align: justify;
    color: white;
  }
  table{
    width: 88%;
    margin-left: auto;
    margin-right: auto;
  }
  center{
    margin-top: 5%;
    font-family: 'Bask' !important;
    color: white;
  }
 a{
    color: #7998b5 !important;
  }
  a:visited{
    color: #F67E7D !important;
  }
  
}
