css:
@-moz-document domain("qbo.intuit.com") {
.dgrid-cell {
	line-height: 16px !important;
}
.dijitSelect {
	height: 18px !important;
}
.dijitInputInner {
	height: 18px !important;
}
.dijitButtonNode {
	height: 18px !important;
}
.rowSpacer {
	margin-top: 2px !important;
}
.dropDownImage {
	padding: 3px 9px 4px 9px !important;
}
.dijitReset .dijitMenuItem {
	padding: 0px 8px !important;
}
.comboboxItemName {
	margin: 2px !important;
	padding-top: 0px !important;
}
._klass {
	display: none;
}
.subsection14TitleText {
	display: none;
}
.dijit.dijitReset.dijitInline.columnSpacer.dijitCheckBox.dijitCheckBoxChecked.dijitChecked {
	display: none;
}
.dijitTextArea {
	padding: 0px 2px;
}
.companyLogo {
	display: none;
}
.homepage .date {
	display: none;
}
timetracking-footer {
	display: none;
}
.companyName {
	display: none;
}
.form-styles .statusSection .amount {
	font-size: 2.0rem !important;
	height: 2.0rem !important;
}
.form-styles .statusSection {
	transition: padding 0.5s !important;
}
.form-styles .subStage {
	padding: 0px 25px 15px 25px !important;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input {
	position: relative;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div:nth-child(1) {
	width: 250px;
	display: block;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div > div {
	width: 99%;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div > span {
	display: block;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div > span > div {
	width: 99%;
	margin: 2px 0 1px 0
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div:nth-child(2) {
	width: 250px;
	margin: 0;
	display: block;
	padding-right: 100px;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div:nth-child(3) {
	width: calc(100% - 250px);
	display: block;
	position: absolute;
	top: 0;
	left: 255px;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input:not(ha-text-field):not(ha-date-picker) > input[type="text"] {
	height: 20px;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input .dijitButtonNode {
	height: 20px;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input .dijitButtonNode .dropDownImage {
	padding: 4px 9px 5px 9px;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div:nth-child(3) > div:nth-child(1) {
	margin: 0;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .dgrid-input > div:nth-child(3) > div:nth-child(2) {
	position: absolute;
	top: 36px;
	left: -82px;
}
.time .timesheetGrid .dgrid .dgrid-column-compositeDetails .descriptionWidth {
	height: 82px !important;
	margin-top: -8px;
	padding: 5px 8px;
	line-height: 1.3;
}
.time .timesheetGrid .dgrid .dgrid-content .dgrid-row-table .dgrid-cell {
	padding-top: 8px;
	padding-bottom: 9px;
}
.time .timesheetGrid .dgrid .dgrid-content .dgrid-row-table .dgrid-cell:nth-child(2) {
	min-width: 677px;
}
.timesheetGrid .dgrid.dgrid-03 .dgrid-row:nth-child(2n) {
	background: #eee;
}
.dgrid-header {
	background: #eee;
}
}
