css:
@-moz-document regexp("https*(://).+(.gamersky.com/)((hardware)|(news)|(tech)|(ent)|(wenku)|(handbook)|(otaku)/).+"){
.Mid0,
.QZnav,
.Mid1Search,
.bgAdWrap,
.fenxiang,
.Comment,
.Bot,
.Mid2_R,
.nav1,
.nav2,
.tc,
.topnav,
.MidL_tit{
display:none;
}

.topnav2{
    border:none;
    margin-top:-50px;
}
.header{
    border:none;
}
.Mid2_L{
margin-left: 170px;
}
.Mid1nav{
margin-left: 170px;
    width: 650px;
}
.Mid2L_tit h1{
font-size:28px;
    line-height: 36px;
    margin-bottom:40px;
    margin-top:40px;
    text-shadow: 4px 4px 8px rgba(0, 0, 0, 0.2);
    color:#7b1d00;
    width: 600px;
    padding-left: 5px;
    padding-right: 5px;
}
.Mid2L_con{
	margin-top: 10px;
    line-height: 36px;
    font-size: 18px;
    font-family:Arial,'Microsoft YaHei',Tahoma,Helvetica,SimSun;
}
.Mid2L_con p{
    padding: 10px;
}
.Mid2L_con p img{
    border: rgba(0, 0, 0, 0.2) 1px solid !important;
    padding: 5px;
    box-shadow: 4px 8px 10px rgba(0, 0, 0, 0.07);
}
.Top .Top_2{
background:none;
        margin-top:-50px;
}
}
@-moz-document regexp("(http://acg.gamersky.com/)((news)|(otaku)/).+"){
.Mid_top,
.Rimg,
.txt-list,
.MidRtit,
.picTwo,
.MMRY,
.Img_txt{
display:none;
}
@-moz-document url("http://www.gamersky.com/"){
#adscontainer_banner_new_top_index_1060_2,
#adscontainer_banner_new_middle_index_1060_1,
#adscontainer_banner_new_middle_index_1060_5,
#adscontainer_banner_new_bottom_index_1060,
.bgAdWrap{
	display:none;
}
}
}
