css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("evernote.com") {
  
  /* Sidebar */
  #gwt-debug-sidebar { 
    width: 43px !important; 
    padding: 4px 4px !important;
  }
  #gwt-debug-stage { margin-left: 43px !important; }
  
  #gwt-debug-sidebar img/*,
  #gwt-debug-Sidebar-newNoteButton img,
  #gwt-debug-Sidebar-newCalendarNoteButton img,
  #gwt-debug-Sidebar-searchButton img,
  #gwt-debug-workChatToast img,
  #gwt-debug-Sidebar-workChatButton img*/
  {
    height: 33px !important;
    width: 33px !important;
    margin: 2px !important;
  }
  
  #gwt-debug-sidebar > div:first-child > div:nth-of-type(1) { height: 40px !important; }
  #gwt-debug-sidebar > div:first-child > div:nth-of-type(2) { margin-bottom: 10px !important; }
  #gwt-debug-sidebar > div:first-child > div:nth-of-type(2) div:not([id]) { height: 39px !important; }
  #gwt-debug-sidebar > div:first-child > div:nth-of-type(3) { margin-bottom: 10px !important; }
  #gwt-debug-sidebar > div:first-child > div:nth-of-type(3) div:not([id]) { height: 39px !important; }
  
  #gwt-debug-sidebar > div:nth-of-type(3) { padding: 5px !important; }
  
  /* Notes */
  #gwt-debug-NotesHeaderView-root > div { padding-top: 8px !important; } /* NOTES */
  #gwt-debug-NotesHeaderView-root > div > div:nth-of-type(2) { top: 5px !important; } /* UPGRADE */
  
  .focus-NotesView-Subheader { margin-top: 13px !important; }  
  .focus-NotesView-Note { height: 85px !important; }
  .focus-NotesView-Note .focus-NotesView-Note-snippetContent {
    left: 15px !important;
    right: 15px !important;
    top: 6px !important;
    bottom: 6px !important;
  }
  .focus-NotesView-Note .focus-NotesView-Note-date { margin-bottom: 5px !important; }
  
  /* Title fonts */
  /*[[fontstyle]]*/
  
  /* main col2 */
  #gwt-debug-MetaBarView-root { padding: 0 15px !important; }
  #gwt-debug-NoteContentView-root > div > div > div { padding: 0 15px !important; }
  
  /* Notebooks widget */
  #gwt-debug-NotebooksDrawer-title {
    padding-top: 8px !important;
    padding-bottom: 8px !important;
  }
  #gwt-debug-NotebooksDrawer-createNotebookButton { top: 5px !important; }
  #gwt-debug-NotebooksDrawerView-root > div:nth-of-type(4) { top: 86px !important;  }
  
  /* Tags wddget */
  .focus-drawer-TagsDrawer-TagsDrawer-header {
    margin-top: 8px !important;
    margin-bottom: 8px !important;
  }
  .focus-drawer-TagsDrawer-TagsDrawer-create-tag-icon { top: 5px !important; }
  .focus-drawer-TagsDrawer-TagHeader-tag-header { margin-top: 5px !important; }
  /*.focus-drawer-TagsDrawer-TagsList-tags-container { column-count: 2 !important; }*/
}
