css:
@-moz-document domain("www.spigotmc.org"){
:root {

        /* Credit to games647 for writing most of the code*/

	/* Colors */

	--bg-color: #222;
	/* Black */
	
	--bg-secondary-color: #383838;
	/* dark grey */
	
	--txt-color: orange;
	--txt-secondary-color: grey;
	/* This will be either none or initial */
	
	--display-banner: initial;
}
/* Spigot banner with the logo and the info text between the navigation bar and the user info bar */

#logoBlock {
	display: var(--display-banner);
}
/* Background */

body {
	background-color: var(--bg-color) !important;
	/* The default text color is black -> grey will make it readable without
        overriding spigot style settings*/
	
	color: var(--txt-secondary-color);
}
/* Navigation header subcategories - i.e. "Your Resources" */

.navTabs .navTab.selected .tabLinks a {
	/* makes the text unreadable if it has a black background */
	
	text-shadow: none !important;
}
/* Subcategories - as well as the selected main category item "Resources" */

.navTabs .navTab.selected .tabLinks,
.hasTabLinks #navigation .navTabs .navTab.selected .navLink {
	background-color: var(--bg-color) !important;
	/* Remove the white border line between the subcategories and the main content */
	
	border: 0px;
}
/* Remove transition border of the selected tab */

#navigation .navTabs .navTab.selected .navLink:before,
#navigation .navTabs .navTab.selected .navLink:after {
	background: initial;
}
/* Drop down menu tab */

.tabMenu,
.tabMenu .menuHeader,
.tabMenu .blockLinksList {
	background-color: var(--bg-color) !important;
	border-color: var(--bg-secondary-color);
}
/* Main content container */

.mainContainer_noSidebar,
.mainContainer .mainContent {
	background-color: var(--bg-color) !important;
	/* Remove the padding for more content space */
	
	padding: 0;
	border: 0;
}
/* Main news site of this website */

.articleItem .sectionMain,
.articleItem .sectionMain .subHeading,
.articleItem .sectionMain .primaryContent,
.articleItem .sectionMain .sectionFooter {
	background-color: var(--bg-secondary-color) !important;
	border: 0;
}
/* Main page news pager */

.articlePager {
	background-color: var(--bg-secondary-color) !important;
	border: 0;
}
/* staffHeading := Staff category section on the Team tab like Administrator, Moderator */
/* resourceInfo := Resource title on the project site */

.staffHeading,
.resourceInfo {
	color: var(--txt-color);
}
/* Team entries */

.memberListItem {
	background-color: var(--bg-secondary-color) !important;
	/* White background border */
	
	border-bottom: 0;
}
/* Image background color if transparent or smaller */

.resourceIcon img,
.avatar img {
	background-color: var(--bg-color) !important;
}
/* Sidebar boxes including on the Resources tab */

.resourceListSidebar .secondaryContent,
.sidebar .secondaryContent {
	background-color: var(--bg-secondary-color) !important;
	/* Removes white border on the resources sidebar */
	
	border: 0 !important;
}
/* Resource entries on the resource tab */

.resourceListItem,
.resourceListItem .resourceImage,
.resourceListItem .resourceStats {
	background-color: var(--bg-secondary-color) !important;
}
/* Header and footer of the main content in the resource tab or "Watched Resources" */

.resourceListMain,
.sectionMain {
	background-color: var(--bg-secondary-color) !important;
	border: 0;
	padding-top: 5px;
}
/* The last entry shouldn't have a white border because we no longer have an white outer container where it could hide this border line */

.resourceList .resourceListItem:last-child {
	border-bottom: 0;
}
/* Resources page main content */

.section .resourceUpdate,
.likesSummary,
.rateBlock {
	background-color: var(--bg-secondary-color) !important;
	border: 0 !important;
}
/* Review*/

.review {
	background-color: var(--bg-secondary-color) !important;
	padding-left: 5px;
	padding-top: 5px;
	border: 0px;
}
/* Make the resource page headings under the main content readable */

.reviews .textHeading,
.updates .textHeading {
	color: var(--txt-color);
}
/* Review response from the author */

.review .messageResponse .comment {
	background-color: var(--bg-color) !important;
	border: 0px;
}
/* Rating bar - Side note:=> What is dark here in the default scheme? */

.dark_postrating {
	background-color: var(--bg-secondary-color) !important;
}
/* Discussions forum post */

.message {
	background-color: var(--bg-secondary-color) !important;
	/* has a border but seems to be a good separator between the posts */
	
	border-color: grey;
}
/* Forum post author info on the discussion thread */

.messageUserInfo,
.avatarHolder,
.userText {
	background-color: var(--bg-color) !important;
	border: 0;
}
/* BB-Codes */

.bbCodeBlock,
.bbCodeBlock .quoteContainer,
.bbCodeBlock .type,
.bbCodeBlock .code {
	background-color: var(--bg-color) !important;
	border: 0;
}
/* Message editor popup heading and footer */

.InlineMessageEditor .sectionFooter,
.InlineMessageEditor .redactor_toolbar {
	background-color: var(--bg-secondary-color) !important;
}
/* Remove white border inside the message editor popup */

.InlineMessageEditor .messageContainer,
.redactor_box,
.redactor_toolbar,
.redactor_box iframe {
	border: 0;
}
/* Watched categories header */

.sectionMain .subHeading {
	background-color: var(--bg-secondary-color) !important;
	border-top: 0;
}
/* Watched categories content */

.sectionMain .categoryList .categoryListItem .categoryInfo,
.sectionMain .sectionFooter {
	background-color: var(--bg-secondary-color) !important;
	border-top: 0;
}
/* Wiki */

.wikiContent .primaryContent {
	background-color: var(--bg-secondary-color) !important;
	border-bottom: 0;
}
/* Resource overview, updates, reviews... */

.mainContainer_noSidebar, .mainContainer .mainContent {
    background-color: #383838 !important;
    padding: 0;
    border: 0;
}

.innerContent .customResourceFields.aboveInfo {
    border-bottom: 0px solid rgb(250,250,250); 
    margin-bottom: 10px;
}

.tabs li a,
.tabs.noLinks li {
	background-color: #383838;
	border: 1px solid rgb(0, 0, 0);
	color: rgb(169, 169, 169);
}
.tabs li.active a,
.tabs.noLinks li.active {
    background-color: #383838;
    color: white;
}

.tabs li:hover a,
.tabs.noLinks li:hover {
	background-color: #383a3d;
	color: white;
}
.tabs{
	border-bottom: 1px solid rgb(0, 0, 0);
}
.dataTable tr.dataRow th {
	background: rgb(34, 34, 34);
	color: rgb(255, 255, 255);
}
.secondaryContent {
	background-color: #3e3e3e;
}
.secondaryContent a:hover {
	color: rgb(144, 142, 142);
	text-decoration: underline;
}
.dataTable .dataRow .dataOptions a.secondaryContent {
	border-left: 1px solid #9a9a9a;
}
.dataTable .dataRow .delete {
	background-color: #222222;
	border-left: 1px solid #9a9a9a;
}
.primaryContent {
	background-color: #3e3e3e;
}
/* Profile */

.PreviewToolTip{
	background: black;
}

.profilePage {
    background: #3e3e3e;
}
.profilePage .tabs.mainTabs {
	background: #3e3e3e;
}
.profilePage .mast {
    /* border-right: 0; */
}
.profilePage .tabs.mainTabs li a {
	background-color: #3e3e3e;
}
.profilePage .tabs.mainTabs li.active a {
	background-color: #3e3e3e;
}
.profilePage .tabs.mainTabs li:hover a {
	background-color: #383a3d;
}
.subHeading {
	border-top: 1px solid rgb(204, 204, 204);
	border-bottom: 1px solid rgb(34, 34, 34);
	background-color: rgb(62, 62, 62);
}
.section {
	margin: 12px auto;
}
.Menu .primaryContent {
	background-color: rgb(62, 62, 62);
	border-bottom: 1px solid rgb(34, 34, 34);
}
.Menu .secondaryContent {
	background-color: rgb(62, 62, 62);
}
.Menu .sectionFooter {
	background-color: rgb(34, 34, 34);
}
.explain .CurrentStatus {
	color: rgb(253, 253, 253);
}
.xenOverlay .formOverlay {
	background-color: rgb(39, 39, 39);
}
.xenOverlay .formOverlay .textCtrl {
	background-color: rgb(39, 39, 39);
	color: rgb(255, 255, 255);
}
.larger.textHeading,
.xenForm .sectionHeader,
.larger.textHeading a,
.xenForm .sectionHeader a {
	color: rgb(255, 255, 255);
}
.navigationSideBar>ul {
	background-color: #383838;
	border: solid 0px rgb(56, 56, 56);
}
.profilePage .primaryUserBlock .userStatus {
	color: white;
}
body,
div,
dl,
dt,
dd,
ul,
ol,
li,
h1,
h2,
h3,
h4,
h5,
h6,
pre,
code,
form,
fieldset,
legend,
input,
button,
textarea,
p,
blockquote,
th,
td {
	color: #9e9e9e;
}

/* Home */
a.button {
    transition-duration: 0.4s;
    display: inline-block;
    background: #222222;
    color: rgb(83,83,83);
}

a.button:hover{

}
/* QuickSearch */

.primaryContent a:hover {
    color: #efb61c;
}

#QuickSearch .formPopup .secondaryControls {
	background: rgb(62, 62, 62);
	color: rgb(167, 167, 167);
}
.textCtrl {
	background-color: #222222;
	color: #ffffff;
}

#caltitle {
    color: rgb(240, 240, 240);
    width: 178px;
}

#calroot{
	background-color: #303439;
}
/* Forum */

.discussionList .sectionHeaders a span:hover {
    background-color: rgb(83, 83, 83);
}

.hasJs .bbCodeSpoilerText{
	background-color: #222222;
}

.bbCodeBlock pre, .bbCodeBlock .code{
	background-image: -webkit-repeating-linear-gradient(top,rgba(0, 0, 0, 0.22) 0px,rgba(0, 0, 0, 0.16) 26px,rgba(0, 0, 0, 0.08) 26px,rgba(0, 0, 0, 0.03) 52px);
}

select, input, button, textarea, button {
    background: #383838;
    color: white;
	border-color: #656565;
}

input[type=search], input[type=text] {
    border-style: ridge;
    border-color: #656565;
}

.uix_icon {
	color: #e6e6e6;
}
#logoBlock .spigot_colorOverlay {
	background-color: rgba(21, 21, 21, 0.8);
}
#navigation .navTabs,
.breadcrumb,
.uix_breadCrumb_toggleList li.toggleList_item {
	background: #3c4046 linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0) 100%);
}
.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3,
.resourceListSidebar h3 {
	background: #4f5258 linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0) 100%);
}
.PageNav a {
	background-color: rgb(56, 56, 56);
	border: 1px solid rgb(111, 111, 111);
}
.nodeList .categoryStrip .nodeTitle a {
	color: rgb(158, 158, 158);
	text-shadow: none;
}
.nodeList .categoryStrip .nodeTitle {
	background: #505050;
}
body .muted a:hover {
	color: rgb(127, 127, 127);
	text-decoration: underline;
}
body .faint:hover,
body a.faint:hover,
body .faint a:hover {
	color: #bbbbbb;
	text-decoration: underline;
}
.nodeList .categoryStrip {
	background: #505050;
}
.nodeList .categoryStrip .nodeDescription {
	color: rgb(241, 238, 238);
	text-shadow: none;
}
.node .unread .nodeText .nodeTitle {
	color: rgb(208, 208, 208);
}
.node .unread .nodeText .nodeTitle a:hover {
	color: rgb(208, 208, 208);
	text-decoration: underline;
}
.node .nodeLastPost {
	background-color: #353535;
}
.discussionListItem {
	background-color: rgb(58, 62, 68);
}
.discussionListItem:nth-child(even) {
	background-color: #3e3e3e;
}
.discussionList .discussionListItem.sticky {
	font-style: italic;
	background-color: rgb(31, 51, 72);
	border-color: rgb(204, 204, 204);
}
.discussionList .sectionHeaders {
	background-color: rgb(62, 62, 62);
}
.dark_postrating_detail {
	background: #3e3e3e;
}
.sectionFooter {
	background-color: rgb(62, 62, 62);
	color: rgb(255, 255, 255);
}
.dark_postrating_column {
	border: 1px solid rgb(62, 62, 62);
}
.dark_postrating_header {
	border-bottom: 0px
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
	background-color: rgb(53, 53, 53);
}
.node .forumNodeInfo .nodeIcon,
.node .categoryForumNodeInfo .nodeIcon {
	background: #6d6d6d url(http://i.imgur.com/7E9f5Ik.png) no-repeat 0 0;
}
.node .forumNodeInfo.unread .nodeIcon,
.node .categoryForumNodeInfo.unread .nodeIcon {
	background: #adadad url(http://i.imgur.com/7E9f5Ik.png) no-repeat 0 0;
}
.forum_list .nodeList .nodeList,
.forum_view .nodeList,
.category_view .nodeList,
.watch_forums .nodeList {
	border: 1px solid rgb(53, 53, 53);
}
.nodeList .node.level_2:nth-child(even) .nodeInfo,
.nodeList .node.level_2:nth-child(even) .nodeInfo .nodeLastPost {
	background: rgb(53, 53, 53);
}
.node.level_2 .nodeInfo {
	border-top: 1px solid rgb(107, 107, 107);
}
.username .style17 {
	color: #a909a2;
}
.pollBlock .pollOption label:hover {
	background: rgb(84, 81, 81);
}
.subHeading a:hover{
    color: rgb(170, 170, 170);
}
.node .nodeText .nodeTitle {
    font-weight: bold;
    font-size: 14px;
    font-family: Ubuntu, sans-serif;
    color: rgb(170, 170, 170);
}
.nodeTitle a:hover{
    color: rgb(170, 170, 170);
}

.lastThreadTitle span{
    color: rgb(190, 190, 190);
}

}
