@-moz-document domain("longzhu.com") {
/* 滚动条美化 */
/* Let's get this party started */

::-webkit-scrollbar {
    width: 10px;
    height: 10px;
}
/* Track */

::-webkit-scrollbar-track {
    /* -webkit-box-shadow: inset 0 0 5px rgba(255, 153, 0, 0.6);*/
    
    background-color: #F5F5F5;
}
/* Handle */

::-webkit-scrollbar-thumb {
    background-color: #FF9900;
    background-image: -webkit-linear-gradient(45deg, rgba(255, 255, 255, .2) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, .2) 50%, rgba(255, 255, 255, .2) 75%, transparent 75%, transparent);
}
}

@-moz-document domain("star.longzhu.com") {
.stage-container .stage-container-head {
    width: auto;
    height: auto;
}
.stage-container-head>.stage-container-left {
    padding-right: 0px;
}
.stage-containe stage-container-right {
    width: 0px;
    height: 0px;
    border-left-width: 0px;
}
}

@-moz-document domain("longzhu.com") {
/*雅黑字体*/

*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var) {
    font-family: "Microsoft Yahei" !important;
    text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}
pre,
code,
kbd,
samp,
var {
    font-family: "Monaco", "Consolas", "Microsoft Yahei" !important;
    text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}
/*去主播认证*/

.certification-dialog {
    display: none !important
}
/*去黑幕*/

.certification-dialog-bg {
    display: none !important
}
}
