css:
@-moz-document domain("0chan.hk") {
html body {
  background-color: #2e343e;
}
body {
  color: lightgray;
}
/* измененные цвета */
* {
    --color-posts: #2E343E;
    --color-back: #242630;

    --color-text: #BCBCBC;

    --color-green: #16A085;
    --color-green-dark: #166958;
    --color-cyan: #81F8E0;
}

/* оформление поста, формы с новым постом */
.block.post,
.reply-form {
    background-color: var(--color-posts) !important;
    border: 1px solid #222222 !important;
    box-shadow: 0 1px 4px #111111 !important;
}

.reply-form-message {
    color: var(--color-text);
    background-color: var(--color-back) !important;
    border: 1px solid var(--color-green) !important;
    box-shadow: unset !important;
}

/* текстовое поле */
textarea {
    color: var(--color-text);
    background-color: var(--color-back);
}

/* разделитель тредов */
div.separator {
    border-color: var(--color-text);
}

/* background */
#content {
  background: #242630;
  background-image: url(http://priscree.ru/img/ba036fa3c99610.png); /*Or http://priscree.ru/img/1b3185b3a3c2c8.png */
  background-size: 200px;
  background-repeat: no-repeat;
  background-attachment: fixed;
  background-position: right bottom;
}


/* ОП-пост*/
div.block.post-op {
    border: 1px solid var(--color-green) !important;
}

/* текст постов (включая ОП) */
div.post-body {
    color: var(--color-text) !important;
}

/* название треда в ОП-посте */
/* (наверное, можно увеличить немного )*/
span.post-id {
    color: var(--color-text);
}

/* футер поста (кнопки скрыть, ответить) */
div.post-footer {
    border: unset !important;
}

/* плашка с названием доски вверху страницы */
div.headmenu {
    background-color: var(--color-back);
    border-bottom: none;
}

/*test */
.sidebar {
  z-index: 100;
}
.sidebar .sidemenu-boards-list-box .sidemenu-boards-list {
  overflow-x: hidden;
}

/* кнопочки */
button.btn.btn-xs {
    color: var(--color-text);
    background-color: var(--color-green-dark);
    border-color: var(--color-green-dark);
}

/* border off1 */
.alerts-item {
  margin-top: 15%;
}
.threads-scroll-spy{
  display: none;
}
.thread-reply-chain {
  display: none;
}
.separator {
  border-top: 1px solid #16a085;
  border-bottom: 1px solid #16a085;
}
.block {
  background: #3f424f none repeat scroll 0 0;
  border: 1px solid #16a085;
  border-radius: 2px;
  box-shadow: 0 1px 4px #16a085;
}

/* post options */
.post .post-footer post-date {
  border-top: 1px solid #16a085;
  color: #16a085;
}
.post .post-header .post-date {
  color: #16a085;
}
.post .post-header .post-thread-options .active {
  color: #16a085;
}
.post .post-body .post-referenced-by, .post .post-body .post-replied-by {
  color: #BCBCBC;
}

/* other1 */
mark, .spoiler {
  background: rgba(204, 204, 204, 0.25) none repeat scroll 0 0;
}
.empty-page {
  text-shadow: 0 0 0;
}
.panel {
  background-color: #2e343e;
}
.panel-default > .panel-heading {
  background-color: #2e343e;
  border-color: #2e343e;
  color: #ccc;
}
.panel-footer {
  background-color: #2e343e;
  border-top: 1px solid #2e343e;
}
.panel-default {
  border-color: #16a085;
}
.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a:hover, .sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a.active {
  background: #3f424f none repeat scroll 0 0;
  border-color: #3f424f;
  border-radius: 30px;
}
.post-img figcaption {
  background: #16a085 none repeat scroll 0 0;
}
.post-img .post-img-buttons {
  background: #16a085 none repeat scroll 0 0;
}
.pull-left {
  float: left;
  color: black;
}
.captcha-form {
  background: #2e343e none repeat scroll 0 0;
}
.captcha-form input {
  border: 1px solid #16a085;
  background-color: #3f424f;
}
.captcha-form-wrapper {
  background: rgba(0, 0, 0, 0.5) none repeat scroll 0 0;
}
.login-form input {
  border: 1px solid #16a085;
  background-color: #3f424f;
}
.post-highlighted {
  box-shadow: 0 0 20px #3ccd9d inset, 0 1px 4px #3ccd9d !important;
}
.im-box .dialog-view {
  background: #2e343e none repeat scroll 0 0;
}
.input-group .form-control:first-child, .input-group-addon:first-child, .input-group-btn:first-child > .btn, .input-group-btn:first-child > .btn-group > .btn, .input-group-btn:first-child > .dropdown-toggle, .input-group-btn:last-child > .btn:not(:last-child):not(.dropdown-toggle), .input-group-btn:last-child > .btn-group:not(:last-child) > .btn {
  background-color: #3f424f;
}
.has-feedback .form-control {
  background-color: #3f424f;
}
.list-group-item:last-child {
  background-color: #3f424f;
}
.form-control {
  background-color: #3f424f;
  color: #fff;
}

/* sp */
mark, .spoiler {
    color: var(--color-text);
    background: var(--color-text);
}

/* head */
.headmenu {
  position: fixed;
  z-index: 99;
  background: #2e343e none repeat scroll 0 0;
  border-bottom: 1px solid #1abc9c;
}
.headmenu-title {
  right: 100px;
}
.headmenu-title span, .headmenu-title a {
  color: #16a085 !important;
}
/* блок кода */
code {
  background: #ccc none repeat scroll 0 0;
}

}
