css:
@-moz-document domain("freecodecamp.com") {
/* Elements */

form {
    background: #444444 !important;
}
hr {
    color: #1e1e1e !important;
    background: #1e1e1e !important;
}


i {
    color: #00ff87  !important;
}

input {
    background: #383838 !important;
    color: #444444 !important;
}
thead {
    color: #a8a19f !important;
    
}
th {
    color: #a8a19f !important;
   
}
tr {
   
    color: #00ff87 !important;
   
}


}

@-moz-document domain("freecodecamp.com") {
/* Classes */

.landing-heading {
    color: #407ee7;
}
.btn-block,
.btn-cta,
.signup-btn.btn,
.signup-btn,
.btn-big,
.btn-primary,
.btn-group,
.btn,
.input-group,
.btn-group-justified,
.button {
    text-shadow: none;
    background: #383838 !important;
    color: #00ff87 !important;
    
}
.landing-skill-icon,
.font-awesome-padding,
.fa-database,
.custom-landing-skill-icon {
    color: #00ff87 !important;
}
.black-text, .night a {
    color: #ffffff;
}
.row,
.night {
 background-image: url("https://static1.squarespace.com/static/562af2d9e4b072888c253383/t/580d408cf7e0ab87773a8a20/1477263506083/002-blurred-background-texture-vol2blue.jpg?format=2500w");
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;
    color: #ededed;
    font-size: 1.2em;

}
.modal-content,
.modal-dialog {
    
    color: #303030 !important;
}
.modal-header,
.challenge-list-header {
    background: #383838 !important;
    color: #00ff87 !important;
}
.completion-icon,
.big-success-icon {
    color: #00ffae !important;
}
.big-error-icon {
    color: #f22c40 !important;
}
.CodeMirror-line {
    color: #e6e2e0 !important;
}
.CodeMirror-gutters,
.CodeMirror-gutter,
.CodeMirror-lint-markers {
    background: #2c2421 !important;
    color: #e6e2e0 !important;
}
.cm-tag,
.cm-bracket {
    color: #00ad9c !important;
}
.cm-keyword {
    color: #f22c40 !important;
}
.cm-comment {
    color: #766e6b !important;
}
.cm-atom,
.cm-variable,
.cm-number {
    color: #6666ea !important;
}
.cm-error {
    background: #f22c40 !important;
    color: #e6e2e0 !important;
}
.cm-attribute,
.cm-property {
    color: #5ab738 !important;
}
.cm-string {
    color: #d5911a !important;
}
.map-aside {
    background: #e0e0e0 !important;
    color: #303030 !important;
  
}
.input-group,
.map-filter,
.input-group {
    background: #383838 !important;
    color: #766e6b !important;
    
}
.input-group-addon {
    background: #5ab738 !important;
    
}

.btn-primary {
    background: #383838 !important;
}
.fa-search {
    color: #e6e2e0 !important;
}
/*
.map-aside-action-bar {
    background: #303030 !important;
    color: #e6e2e0 !important;

.map-aside-action-item, .map-aside-action-collapse, .map-aside-action-pop-out {
    background: #303030 !important;
    color: #766e6b !important;
}
*/

.map-accordion h2 > a {
    background-color: #383838 !important;
}


.fa-caret-down {
    background: transparent !important;
    color: #766e6b !important;
}
.challengeBlockTime {
    color: #766e6b !important;
}
.text-info,
.text-primary {
    color: #d5911a !important;
}
.ion-checkmark-circled {
    color: #5ab738 !important;
}
.disabled,
.ion-locked {
    color: #766e6b !important;
}
.profile-social-icons,
.fa-github-square {
    color: #407ee7 !important;
}
.cal-heatmap-container,
.domain-background,
.graph-domain,
.graph-label {
    background: #303030 !important;
    color: #766e6b !important;
}
.graph-rect {
    fill: #2c2421 !important;
}
.r4 {
    fill: #00ff87 !important;
}
.r3 {
    fill: #00ff87 !important;
    opacity: 0.75 !important;
}
.r2 {
    fill: #00ff87 !important;
    opacity: 0.5 !important;
}
.r1 {
    fill: #00ff87 !important;
    opacity: 0.25 !important;
}
.gitter-chat-embed,
.gitter-chat-embed-action-bar {
    background: #303030 !important;
    color: #766e6b !important;
}
.chat-embed-main-title {
    color: #e6e2e0 !important;
}
.gitter-chat-embed-action-bar-item,
.gitter-chat-embed-action-bar-item-pop-out,
.gitter-chat-embed-action-bar-item-collapse-chat {
    color: #407ee7 !important;
    opacity: 1 !important;
}
.gitter-chat-embed-loading-indicator,
.gitter-icon {
    color: #407ee7 !important;
}
.articleContent,
.markdown,
.article-container,
.wikiSelector,
.wiki-container,
.wiki-aside,
.wiki-aside-embed,
.wiki-aside-action-bar {
    background: #303030 !important;
    color: #766e6b !important;
}
.landing-page {
    color: #766e6b !important;
}
.navbar {
    background: #1e1e1e;
}
.navbar-right {
    background: #1e1e1e;
}
.panel-heading {
    background: #303030 !important;
   
}

.panel-body {
    background-color: #444444;
}
.cm-s-monokai.CodeMirror {
    background-color: #303030 !important;
}
.row ng-scope {
    background: #444444 !important;
}
::-webkit-scrollbar { 
    display: none; 
}
.cm-s-monokai span.cm-def {
    color: #f2ff05;
}
.text-info, .text-primary {
    color: #00ff87 !important;
}

}
