css:
@-moz-document domain("theverge.com") {

/* general */
body {
    background: #000000;
    color: #ffffff;
}

.m-carousel__control {
    background: #1a1a1a;
}
.m-carousel.basic .m-carousel__item h3 {
    background: #000000;
    color: #ffffff;
}

.c-image-gallery__viewer-next, 
.c-image-gallery__viewer-prev,
.c-image-gallery__thumbs-next, 
.c-image-gallery__thumbs-prev {
    background: #1a1a1a;
}

.m-poll {
    background: #000000;
    color: #ffffff;
    border: 1px solid #97979b;
}
.m-poll__totals {
    color: #ffffff;
}
.m-poll__body {
    background: #000000;
    color: #ffffff;
    border-color: #1a1a1a;
}
.m-poll__controls {
    background: #000000;
    border-color: #1a1a1a;
    border-bottom: 1px dotted #ffffff;
}
.m-poll__body.results header h6 {
    color: #ffffff;
}
.m-poll__thermometer {
    background: #1a1a1a;
}

.p-notice-info {
    background: #1a1a1a;
    color: #ffffff;
    border-color: #ffffff;
}


/* 404 */
.p-message-page a svg path {
    fill: #ffffff;
}


/* homepage */
.l-hub-wrapper {
    background: #000000;
}

.c-rock-list__title-wrapper a[href="/tldr"]::after {
    content: 'TLDR';
    display: inline-block;
    margin-bottom: 10px;
    font-size: 42px;
    color: #ffffff;
}
.c-rock-list__title-wrapper a[href="/tldr"] img {
    display: none;
}


/* archives */
.l-page {
    background: #000000;
}

.m-tag-page__content > h1 {
    color: #ffffff;
}

.p-basic-article-list__node .body h3 {
    color: #e5127d;
}
.p-basic-article-list__node .body .byline {
    color: #ffffff;
}
.p-basic-article-list__node .body .byline a {
    color: #7e74d2;
}


/* storystream */
.c-entry-group-labels .c-entry-group-labels__item--stream:first-child svg {
    fill: #ffffff;
}
.c-entry-group-labels.c-entry-group-labels--stream span:nth-child(2) {
    color: #ffffff;
}

.c-entry-hero--content-overlap .c-entry-hero__content {
    background: #1a1a1a;
}
.c-entry-hero--content-overlap .c-entry-hero__content .c-page-title {
    margin-left: 10px;
}

.c-entry-update-bar {
    background: #1a1a1a;
    color: #ffffff;
}
.c-entry-update-bar__follow {
    color: #e5127d;
}

.c-entry-box__date {
    margin-top: 0;
}
.c-entry-box__date-relative {
    color: #7e74d2;
}

.c-pagination {
    background: #1a1a1a;
}
.c-pagination__text {
    color: #ffffff;
}


/* articles */
.l-main-content {
    background: #000000;
}

.c-entry-group-labels__item::before {
    background: #000000 !important;
}
.c-entry-group-labels__item:nth-child(2) {
    color: #7e74d2;
}

h1,
.c-page-title,
.p-rock-head,
.c-entry-hero--feature .c-page-title {
    color: #ffffff;
}

.c-tab-bar {
    background: #1a1a1a;
}
.c-tab-bar__logo svg {
    fill: #ffffff !important;
}

.p-dek,
.c-byline {
    color: #ffffff;
}

.e-image__meta {
    color: #ffffff;
}

.c-entry-sidebar {
    background: #1a1a1a;
    color: #ffffff;
}

.c-rock-list .native-ad-latest,
.c-rock-list__link {
    color: #e5127d;
}

.c-comments__header {
    background: #000000;
}
.c-comments__count span {
    color: #7e74d2;
}
.c-comments__count .p-loader {
    margin: 40px auto;
}
.c-comments__list {
    border-top-color: #97979b;
    border-left-color: #97979b;
    border-right-color: #97979b;
}
.c-comments__message {
    background: #000000;
    color: #ffffff;
}
.m-comments .citem.recommended,
.c-comments__recommended {
    background: #333333;
}
.m-comments .citem.recommended .comment_inner {
    padding: 4px 10px;
}
.c-comments__date {
    color: #ffffff;
}

.c-compact-river__entry {
    background: #000000;
}


/* forums */
.m-forum-index__table tr:nth-child(even) {
    background: #1a1a1a;
}

.m-forum-index__table th:nth-child(1) a, .m-forum-index__table td:nth-child(1) a {
    color: #ffffff;
}

.m-forum-header {
    background: #1a1a1a;
}   

.p-entry-header h1 {
    color: #ffffff;
}

.p-entry-header__comments {
    background: #1a1a1a;
    margin-top: 48px;
}

.m-article__entry a, 
.m-stream__header a {
	background: transparent !important;
}

.m-comments .comments-header {
    background: #000000;
}

.m-comments .citem .cbody {
    color: #ffffff;
}

.m-article__entry blockquote, 
.m-stream__header blockquote,
.m-comments .citem .cbody blockquote {
    background: #1a1a1a;
}

}
