css:
@-moz-document regexp("https://www.baidu.com/\\#?") {
html{
    overflow-y: auto;
}
body {
    background-image: url(https://tuchuang001.com/images/2017/06/16/ie-die1.jpg) !important;
    background-repeat:no-repeat !important;
    background-position: center top !important;
    background-size: cover !important;
    background-attachment: fixed !important;
    font-family:"Microsoft YaHei" !important;
}
html,
body{
    height: 100%;
}
/*修改按钮透明度*/
#head_wrapper.s-down .s_btn_wr, .s_btn_wr {
    background-color: rgba(0, 106, 255, 0.65) !important;
}
#head_wrapper.s-down .btn, .s-title-img .btn{
	background-color:rgba(0, 106, 255, 0.4) !important;
}
#head_wrapper.s-down .btn:hover, #head_wrapper.s-down .btn.btnhover, .s-title-img .btn:hover, .s-title-img .btn.btnhover {
    background-color: rgba(49, 126, 243, 0.8) !important;
}
}

@-moz-document regexp("https?://www.baidu.com/s\\?(.*)") {
body {
    background-image: url(https://tuchuang001.com/images/2017/06/16/ie-die.jpg) !important;
    background-color: black;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-position: center top !important;
    background-size: cover !important;
    font-family:"Microsoft YaHei" !important;
}
#foot {
    background: rgba(245, 246, 245, 0.8) !important;
    border-top: 0px !important;
}
#help {
    background: rgba(245, 246, 245, 0) !important;
}
#content_right{
display:none !important;
}
}
