@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.google.com") {

/* MESSAGE ROWS */
/* ============ */

/* Unread Message Rows */
/* ------------------- */
/* Fixed Highlight for Unread*/
tr[class^="xweT7d QhHSYc"] {background-color: #2883C9 !important;}

/* Hover Highlight for Unread */
table.N2mZ7b tr.QhHSYc:hover, table.tlc tr.ur:hover { background-color: #2883C9 !important;}


/* Read Message rows */
/* ----------------- */
/* Fixed Highlight for Read */
/* .AnqB9d {background-color:#262626 !important;} */

/* Hover Highlight for Read */
table.N2mZ7b tr.AnqB9d:hover, table.tlc tr.rr:hover { background-color: #35405B !important;}


/* Selected Message rows */
/* --------------------- */
/* Fixed Highlight for Selected */
.rfza3e {background-color : #99C415 !important;
color : #000000 !important;}

/* Hover Highlight for Selected */
table.N2mZ7b tr.rfza3e:hover, table.tlc tr.ur:hover { background-color: #99C415 !important;}

/* Message List Row Borders */
/* ------------------------ */
TD
{border-color: #465D8C !important}


/* LINK COLORS */
/* =========== */
.rj1J6b, .vwcZUe, :not(.HhG5wd)>span[role="link"], a:link, a:visited, .link, a:active, .link:active, .vZFPPc, .z8N7nd, .zD5BAe, .l73JSe
{color: #29B5B6 !important;}


/* LEFT NAVIGATION PANEL */
/* ===================== */

/* Hovering Mail Categories (Inbox, Starred, Chats, etc.) */
.ACpQre { margin-right:-2px !important;}
.ACpQre:hover { background-color: #35405B !important;} 

/* Hovering Label Names */
.WnjAod:hover { background-color: #35405B !important;}
.pvSW6e { padding: 0 !important;}
.BFvfre { padding:2px !important; width: 18px !important;}
.WnjAod .yyT6sf { margin: 2px 0px -2px 5px !important;}

/* Hovering Chat List */
.oOukxe:hover { background-color: #35405B !important;}

/* Hovering Quicklinks List */
div[class^="ooCKse"]:hover  a { background-color: #35405B !important; }

}
