css:
@-moz-document domain("momentjs.com") {
body {
    background-color: black;
}
p, h3, h3.docs-method-title > a, code, ul, ul li, ol, ol li {
    color: white;
}
pre, code {
    background-color: #121214;
}
code > span.comment {
    color: gray;
}
.is-moment .hero, .is-moment-timezone .hero {
    background-color: #7461b2;
    box-shadow: inset 0 10px 10px #7461b2;
}
.is-moment .docs-section-title, .is-moment-timezone .docs-section-title {
    background-color: #7461b2;
}
.nav-project-moment.active, .nav-project-moment-timezone.active {
    background-color: #7461b2;
}
.nav-project {
    color: #7461b2;
}
.is-moment .nav-pages a, .is-moment-timezone .nav-pages a {
    color: #7461b2;
}
.is-moment .nav-pages a.active, .is-moment-timezone .nav-pages a.active {
    background-color: #7461b2;
}
.is-moment .docs-nav-item, .is-moment-timezone .docs-nav-item {
    color: #7461b2;
}
.is-moment .docs-nav a.active, .is-moment-timezone .docs-nav a.active {
    border-left-color: #7461b2;
}
.is-moment .docs-nav .active a, .is-moment-timezone .docs-nav .active a {
    border-left-color: rgba(116, 97, 178, 0.3);
}
a.docs-nav-section-title {
    color: white;
}
a {
    color: lightblue;
}
.is-moment div.docs-method-signature pre, .is-moment-timezone div.docs-method-signature pre {
    background-color: #4b3d7b;
    border-color: rgba(116, 97, 178, 0.7);
}
.is-moment footer, .is-moment-timezone footer {
    background-color: #7461b2;
}
table, table > tbody, table > thead, table td, table th, table tbody tr td, .docs-method-prose td {
    /* bloody tables */
    
    background-color: black;
    color: white;
}
.is-moment .builds-button-source, .is-moment-timezone .builds-button-source {
    background-color: #4b3d7b;
}
.is-moment .builds-button-source:hover, .is-moment-timezone .builds-button-source:hover {
    background-color: #7461b2;
}
.is-moment .builds-button-gzipped, .is-moment-timezone .builds-button-gzipped {
    background-color: rgba(116, 97, 178, 0.2);
    color: lightgray;
}
.is-moment .builds-button-gzipped span.filesize, .is-moment-timezone .builds-button-gzipped span.filesize {
    color: darkgray;
}
.is-moment .hero-circle, .is-moment-timezone .hero-circle {
    /* wondering why these are named hero, like why? */
    
    background-color: #7461b2;
    ;
}
.locales div.button {
    background-color: #7461b2;
    color: white;
}
.map-wrap {
    background-color: #7461b2;
}
.map-inset {
    /* went and flipped the colors*/
    
    background-image: url(https://i.imgur.com/OFRjJTa.png);
}
}
