css:
@-moz-document url-prefix("http://iourpg.com/forum"), url-prefix("https://iourpg.com/forum") {
#content {
    background: #111 !important;
    width: auto !important;
    padding: 20px 10px;
    overflow: hidden;
}
.trow2 {
    background: #010101 !important;
    border: 1px solid;
    border-color: #444 #333 #333 #444 !important;
}
.trow1 {
    background: #101010 !important;
    border: 1px solid;
    border-color: #444 #333 #333 #444 !important;
}
#panel .lower {
    background: #222;
    color: #999;
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #333 !important;
    padding: 5px;
}
table {
    color: #444 !important;
    font-size: 13px;
}
.trow_sep {
    background: #222 !important;
    color: #555 !important;
    border-bottom: 1px solid #333 !important;
    padding: 6px;
    font-size: 12px;
    font-weight: bold;
}
.tfoot {
    border-top: 1px solid #444 !important;
    padding: 6px;
    background: #222 !important;
    color: #555 !important;
}
.tborder {
    background: none !important;
    width: 100%;
    margin: auto auto;
    border: 1px solid #444 !important;
    padding: 1px;
}
.post .post_author {
    border-bottom: 1px solid #444 !important;
    border-top: 2px solid #444 !important;
    background: #111 !important;
    padding: 5px;
    overflow: hidden;
}
.post_controls {
    clear: both;
    background: #222 !important;
    border-bottom: 1px solid #444 !important;
    padding: 5px;
    overflow: hidden;
}
.postbit_buttons > a:link, .postbit_buttons > a:hover, .postbit_buttons > a:visited, .postbit_buttons > a:active {
    display: inline-block;
    padding: 2px 5px;
    margin: 2px;
    font-size: 11px;
    background: #111 !important;
    border: 1px solid #444 !important;
    color: #555;
}
textarea {
    background: #000 !important;
    color: #555 !important;
    border: 1px solid #444 !important;
    padding: 2px;
    line-height: 1.4;
    outline: 0;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
    font-size: 13px;
}
input.textbox {
    background: #000 !important;
    color: #555 !important;
    border: 1px solid #444 !important;
    padding: 3px;
    outline: 0;
    font-size: 13px;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
}
button, input.button {
    padding: 3px 8px;
    cursor: pointer;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
    font-size: 13px;
    background: #111 !important;
    border: 1px solid #444 !important;
    color: #555 !important;
    outline: 0;
}
#footer .lower {
    color: #666;
    padding: 6px 6px 12px 6px;
    overflow: hidden;
    font-size: 11px;
    background: #111 !important;
}
#footer .upper {
    background: #333 !important;
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important;
    padding: 6px;
    font-size: 12px;
    overflow: hidden;
}
body {
    background: #111 !important;
    color: #333;
    text-align: center;
    line-height: 1.4;
    margin: 0;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
    font-size: 13px;
    overflow-y: scroll;
}
}
