css:
@-moz-document regexp("(http(s|)\\:\\/\\/|)(www\\.|)facebook\\.github\\.io\\/react(.*)") {
html {
    background-color: #060606;
    color: #B7B7B7;
}
.marketing-col h3, .nav-docs h3, #examples h3, .nav-docs a {
    color: #D2D2D2;
}
small code, li code, p code {
    color: #AAAAAA;
    background-color: rgba(100, 100, 100, 0.4);
}
.playground-tab-active {
    color: #DDDDDD;
}
.playgroundCode, .playground-tab, .playgroundPreview {
    border-color: rgba(239, 239, 239, 0.3);
}
.playground-tab {
    background-color: #0E121B;
}
.playgroundPreview {
    background-color: #060606;
}
.cm-s-solarized-light {
    background-color: #060606;
    color: #B7B7B7;
}
div.highlight, .highlight pre code {
    /* can't really style highlightjs */
    
    background-color: #070A13;
    color: #F2F2F2;
}
/* idk how to make so the :after (which says code) is styled */

.hll {
    border-left-color: #FFFFFF;
    background-color: rgba(219, 219, 219, 0.1);
}
.documentationContent blockquote {
    background-color: #070A13;
}
img[src="/react/img/docs/should-component-update.png"] {
    /*because this picture has black text which you can't read with dark bg*/
    
    background-color: #898989;
}
}
