css:
@-moz-document domain("steemit.com") {
/**
 * A dark style for Steemit.com,
 * inspired by night mode on Poloniex.com
 *
 * Author: https://steemit.com/@sethmerrick
 * Version: 1.0.1
 * Last Updated: 2017-06-21
 *
 * GitHub: TBD
 */
/* Global */

body {
  background-color: #0f1414;
  color: #6f9397;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  color: #59989c;
}
a {
  color: #59989c;
}
a:hover {
  color: #32dbbc;
}
a:focus {
  color: #32eccd;
}
hr {
  border-bottom: 1px solid #092f33;
}
img {
  opacity: .7;
}
select {
  background-color: #092f33;
  color: #59989c;
}
/* Header */

.Header,
.Header .menu,
.Header__top,
.Header__top .header,
.Header__sub-nav,
.Header__sub-nav li>a {
  background-color: #263233;
  background-image: -webkit-linear-gradient(#263233, #151b1b);
  background-image: linear-gradient(#263233, #151b1b);
}
.Header,
.Header__top {
  border-bottom: 1px solid #031113;
}
.Header__sub-nav {
  border-bottom: 0px solid #000;
}
.Header__sub-nav .columns {
  padding: 0;
}
.Header__sub-nav li {
  margin: 0;
  padding: 0;
}
.Header__sub-nav li:first-child {
  padding-left: 0;
}
.Header__sub-nav li:first-child>a {
  padding-left: 1.618rem;
}
.Header__sub-nav li:last-child>a {
  padding-right: 1.618rem;
}
.Header__sub-nav li>a {
  border-left: 1px solid #263233;
  border-right: 1px solid #151b1b;
  border-bottom: 0px solid #000;
  color: #59989c;
  font-size: .9rem;
  padding: 1rem 1.618rem;
  text-shadow: 0 1px 1px #031113;
  text-transform: uppercase;
}
.Header__sub-nav li>a:hover {
  background-color: #092f33;
  background-image: none;
  color: #59989c;
}
.HorizontalMenu .active>a {
  background-color: #092f33;
  border-left: 1px solid #092f33;
  color: #59989c;
}
li.Header__userpic .Userpic {
  border: 1px solid #092f33;
}
/* Posts List */

.PostsIndex__topics_compact>select {
  border-bottom: 1px solid #092f33;
}
.PostSummary {
  background-color: #111818;
}
.PostSummary__header>h3>a {
  color: #32dbbc;
}
.PostSummary__header>h3>a:visited {
  color: #5d8286;
}
.PostSummary__image {
  border: 1px solid #109a9a;
}
.Voting__inner,
.VotesAndComments__votes,
.PostSummary__time_author_category .Reblog__button {
  border-right: 1px solid #092f33;
}
.PostSummary__time_author_category {
  border-left: 1px solid #092f33;
}
.VotesAndComments path {
  fill: #555;
}
/* Post */

.PostsList__post_overlay {
  background-color: hsla(185, 20%, 5%, 0.9);
}
.PostsList__post_container,
.PostsList__post_container .PostFull,
.callout {
  background-color: #111818;
  color: #6f9397;
}
.PostFull__body a,
.callout a {
  color: #32dbbc;
}
.Reputation,
.TagList__horizontal a {
  background-color: #092f33;
  border: 1px solid #092f33;
  color: #59989c;
}
.TagList__horizontal a:hover {
  background-color: #2b4f55;
}
.Markdown h1,
.Markdown h2,
.Markdown h3,
.Markdown h4,
.Markdown h5,
.Markdown h6 {
  padding-bottom: 1.618rem;
}
.Markdown code {
  background-color: #092f33;
  border: 1px solid #092f33;
  color: #59989c;
}
.Markdown table thead {
  background-color: #273332;
  color: #83bec0;
}
.Markdown table {
  border-left: 1px solid #273332;
}
.Markdown table th {
  background-color: #273332;
  border-right: 1px solid #111818;
}
.Markdown table th:last-child {
  border-right: 1px solid #273332;
}
.Markdown table td {
  border-right: 1px solid #273332;
  border-top: 1px solid #273332;
}
.Markdown table tr:nth-child(even) {
  background-color: #111818;
}
.Markdown table tr:nth-child(odd) {
  background-color: #0f1414;
}
/* Right Sidebar */

.PostsIndex__topics {
  border-left: 1px solid #092f33;
}
/* Post Editor */

[type=color],
[type=date],
[type=datetime-local],
[type=datetime],
[type=email],
[type=month],
[type=number],
[type=password],
[type=search],
[type=tel],
[type=text],
[type=text]:focus,
[type=time],
[type=url],
[type=week],
textarea,
textarea:focus,
.drag-and-drop {
  background-color: #111818;
  border: 1px solid #273332;
  color: #6f9397;
}
.dropzone textarea {
  border-bottom: 1px dashed #273332;
}
.ReplyEditor__options select {
  border: 1px solid #273332;
}
.ReplyEditor .vframe__section--shrink .button {
  background-color: #092f33;
}
.ReplyEditor .vframe__section--shrink .button.hollow {
  background: none;
  border: 1px solid #092f33;
  color: #6f9397;
  font-weight: bold;
}
.ReplyEditor .Preview .Markdown {
  background-color: #111818;
  border: 1px solid #273332;
  color: #6f9397;
}
/* Search */

.gsc-control-cse {
  background-color: #111818;
  border: 1px solid #273332;
}
.gsc-webResult.gsc-result,
.gsc-webResult.gsc-result:hover {
  border: 1px solid #273332;
}
.gsc-table-result tbody {
  background-color: #111818;
}
.gsc-preview-reviews,
.gsc-control-cse .gs-snippet,
.gsc-control-cse .gs-promotion em,
.gsc-control-cse .gs-snippet,
.gsc-control-cse .gs-promotion em {
  color: #6f9397;
}
.gs-result .gs-title,
.gs-result .gs-title a,
.gs-result .gs-title b {
  color: #32dbbc !important;
}
.Markdown blockquote > p {
  color: rgba(255, 255, 255, 0.6);
}
.menu.vertical > li > a {
  color: #59989c;
  background-color: rgb(51, 51, 51);
}
.dropdown-pane {
  border: 1px solid #59989c;
}
.VerticalMenu > li.title {
  padding: 0.4rem;
  font-weight: 700;
  text-align: center;
  background-color: rgb(51, 51, 51);
  border-bottom: 1px solid rgb(89, 152, 156);
}
table {
    background-color: rgb(89, 152, 156);
}
  
tr {
  background-color:#555; 
}

td {
  background-color: #555;
}

table thead {
    background: #333;
}
  
table tr th:first-child {
  color: rgb(89, 152, 156);
}

pre {
    padding-right: 10px;
}

.Comment .highlighted {
    background: #1B1B1B none repeat scroll 0%;
}
}
